import{_ as e,c as a,o as r,ah as o}from"./chunks/framework.0CRElgwM.js";const u=JSON.parse('{"title":"Date and Time","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/field-types/date-time.md","filePath":"documentation/field-types/date-time.md","lastUpdated":1751799890000}'),d={name:"documentation/field-types/date-time.md"};function i(n,t,s,l,h,m){return r(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="date-and-time" tabindex="-1">Date and Time <a class="header-anchor" href="#date-and-time" aria-label="Permalink to “Date and Time”">​</a></h1><p>The Date and Time field type is used to handle date and time operations, formatting, and calculations. This field type provides several options for working with dates and times, including getting the current date and time, converting dates between different formats, adding time to a date, and subtracting time from a date.</p><p><a href="https://day.js.org/docs/en/display/format" target="_blank" rel="noreferrer">Date Format</a></p><h2 id="field-types" tabindex="-1">Field Types <a class="header-anchor" href="#field-types" aria-label="Permalink to “Field Types”">​</a></h2><p>The Date and Time field type supports several operations for handling dates and times:</p><table tabindex="0"><thead><tr><th>Field Type</th><th>Description</th></tr></thead><tbody><tr><td><a href="#current-date-and-time"><strong>Current Date and Time</strong></a></td><td>Get current date and time</td></tr><tr><td><a href="#change-date-format"><strong>Change Date Format</strong></a></td><td>Convert date from one format to another</td></tr><tr><td><a href="#increase-date"><strong>Increase Date</strong></a></td><td>Add time to a date</td></tr><tr><td><a href="#decrease-date"><strong>Decrease Date</strong></a></td><td>Subtract time from a date</td></tr></tbody></table><h2 id="current-date-and-time" tabindex="-1">Current Date and Time <a class="header-anchor" href="#current-date-and-time" aria-label="Permalink to “Current Date and Time”">​</a></h2><p>Get current date and time.</p><h3 id="options" tabindex="-1">Options <a class="header-anchor" href="#options" aria-label="Permalink to “Options”">​</a></h3><table tabindex="0"><thead><tr><th>Options</th><th>Required</th></tr></thead><tbody><tr><td><a href="#date-and-time-format"><strong>Date and Time Format</strong></a></td><td><strong>Yes</strong></td></tr></tbody></table><h3 id="date-and-time-format" tabindex="-1">Date and Time Format <a class="header-anchor" href="#date-and-time-format" aria-label="Permalink to “Date and Time Format”">​</a></h3><p>Specify the format for the current date and time output. Select from predefined formats or enter a custom format.</p><h2 id="change-date-format" tabindex="-1">Change Date Format <a class="header-anchor" href="#change-date-format" aria-label="Permalink to “Change Date Format”">​</a></h2><p>Convert a date from one format to another format.</p><h3 id="options-1" tabindex="-1">Options <a class="header-anchor" href="#options-1" aria-label="Permalink to “Options”">​</a></h3><table tabindex="0"><thead><tr><th>Options</th><th>Required</th></tr></thead><tbody><tr><td><a href="#date-current-format"><strong>Date Current Format</strong></a></td><td><strong>Yes</strong></td></tr><tr><td><a href="#date-new-format"><strong>Date New Format</strong></a></td><td><strong>Yes</strong></td></tr></tbody></table><h3 id="date-current-format" tabindex="-1">Date Current Format <a class="header-anchor" href="#date-current-format" aria-label="Permalink to “Date Current Format”">​</a></h3><p>Specify the current format of the date that you want to convert. This should match the format of the input date.</p><h3 id="date-new-format" tabindex="-1">Date New Format <a class="header-anchor" href="#date-new-format" aria-label="Permalink to “Date New Format”">​</a></h3><p>Specify the desired output format for the converted date.</p><div class="info custom-block"><p class="custom-block-title">Usage Notes</p><ul><li>If you have given element query in this field then scrape date from element text.</li><li>If you haven&#39;t provided an element query then you must specify date in the field value.</li></ul></div><h2 id="increase-date" tabindex="-1">Increase Date <a class="header-anchor" href="#increase-date" aria-label="Permalink to “Increase Date”">​</a></h2><p>Add a specified amount of time to a date.</p><h3 id="options-2" tabindex="-1">Options <a class="header-anchor" href="#options-2" aria-label="Permalink to “Options”">​</a></h3><table tabindex="0"><thead><tr><th>Options</th><th>Required</th></tr></thead><tbody><tr><td><a href="#date-format"><strong>Date Format</strong></a></td><td><strong>Yes</strong></td></tr><tr><td><a href="#amount-of-time-add"><strong>Amount of Time Add</strong></a></td><td><strong>Yes</strong></td></tr><tr><td><a href="#date-output-format"><strong>Date Output Format</strong></a></td><td><strong>Yes</strong></td></tr></tbody></table><h3 id="date-format" tabindex="-1">Date Format <a class="header-anchor" href="#date-format" aria-label="Permalink to “Date Format”">​</a></h3><p>Specify the format of the input date that you want to increase.</p><h3 id="amount-of-time-add" tabindex="-1">Amount of Time Add <a class="header-anchor" href="#amount-of-time-add" aria-label="Permalink to “Amount of Time Add”">​</a></h3><p>Specify how much time to add to the date:</p><ul><li><strong>Value</strong>: Enter the numerical amount</li><li><strong>Unit</strong>: Select the time unit from the dropdown: <ul><li>Day</li><li>Week</li><li>Month</li><li>Year</li><li>Hour</li><li>Minute</li><li>Second</li><li>Millisecond</li></ul></li></ul><h3 id="date-output-format" tabindex="-1">Date Output Format <a class="header-anchor" href="#date-output-format" aria-label="Permalink to “Date Output Format”">​</a></h3><p>Specify the format for the output date after adding time.</p><div class="info custom-block"><p class="custom-block-title">Usage Notes</p><ul><li>If you have given element query in this field then scrape date from element text.</li><li>If you haven&#39;t provided an element query then you must specify date in the field value.</li></ul></div><h2 id="decrease-date" tabindex="-1">Decrease Date <a class="header-anchor" href="#decrease-date" aria-label="Permalink to “Decrease Date”">​</a></h2><p>Subtract a specified amount of time from a date.</p><h3 id="options-3" tabindex="-1">Options <a class="header-anchor" href="#options-3" aria-label="Permalink to “Options”">​</a></h3><table tabindex="0"><thead><tr><th>Options</th><th>Required</th></tr></thead><tbody><tr><td><a href="#date-format"><strong>Date Format</strong></a></td><td><strong>Yes</strong></td></tr><tr><td><a href="#amount-of-time-subtracted"><strong>Amount of Time Subtracted</strong></a></td><td><strong>Yes</strong></td></tr><tr><td><a href="#date-output-format"><strong>Date Output Format</strong></a></td><td><strong>Yes</strong></td></tr></tbody></table><h3 id="date-format-1" tabindex="-1">Date Format <a class="header-anchor" href="#date-format-1" aria-label="Permalink to “Date Format”">​</a></h3><p>Specify the format of the input date that you want to decrease.</p><h3 id="amount-of-time-subtracted" tabindex="-1">Amount of Time Subtracted <a class="header-anchor" href="#amount-of-time-subtracted" aria-label="Permalink to “Amount of Time Subtracted”">​</a></h3><p>Specify how much time to subtract from the date:</p><ul><li><strong>Value</strong>: Enter the numerical amount</li><li><strong>Unit</strong>: Select the time unit from the dropdown: <ul><li>Day</li><li>Week</li><li>Month</li><li>Year</li><li>Hour</li><li>Minute</li><li>Second</li><li>Millisecond</li></ul></li></ul><h3 id="date-output-format-1" tabindex="-1">Date Output Format <a class="header-anchor" href="#date-output-format-1" aria-label="Permalink to “Date Output Format”">​</a></h3><p>Specify the format for the output date after subtracting time.</p><div class="info custom-block"><p class="custom-block-title">Usage Notes</p><ul><li>If you have given element query in this field then scrape date from element text.</li><li>If you haven&#39;t provided an element query then you must specify date in the field value.</li></ul></div>',45)])])}const c=e(d,[["render",i]]);export{u as __pageData,c as default};
