import{_ as a,c as i,o as l,aj as t}from"./chunks/framework.DRmbE9yB.js";const h=JSON.parse('{"title":"Google Service - Gmail","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/field-types/google-service-gmail.md","filePath":"documentation/field-types/google-service-gmail.md","lastUpdated":0}'),r={name:"documentation/field-types/google-service-gmail.md"};function o(s,e,n,m,c,g){return l(),i("div",null,[...e[0]||(e[0]=[t('<h1 id="google-service-gmail" tabindex="-1">Google Service - Gmail <a class="header-anchor" href="#google-service-gmail" aria-label="Permalink to “Google Service - Gmail”">​</a></h1><p>Integrate with Gmail API to manage emails, fetch messages, and send emails directly from your automation workflows.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to “Overview”">​</a></h2><p>The Google Service - Gmail field types allow you to interact with Gmail, enabling you to read, send, and manage emails as part of your automation processes. This integration provides powerful email automation capabilities with support for search queries, filtering, and message parsing.</p><h2 id="field-types" tabindex="-1">Field Types <a class="header-anchor" href="#field-types" aria-label="Permalink to “Field Types”">​</a></h2><h3 id="fetch-mail-gmail" tabindex="-1"><a href="/documentation/field-types/google-service-gmail/fetch-mail-gmail.html">Fetch Mail (Gmail)</a> <a class="header-anchor" href="#fetch-mail-gmail" aria-label="Permalink to “Fetch Mail (Gmail)”">​</a></h3><p>Retrieve multiple emails from Gmail based on search criteria and filters.</p><p><strong>Features:</strong></p><ul><li>Search emails using Gmail query syntax</li><li>Filter by labels, dates, and senders</li><li>Extract specific email parts (body, subject, from, etc.)</li><li>Support for retry mechanisms</li><li>Regex filtering for data extraction</li><li>Automatic mark as read functionality</li></ul><p><strong>Learn More:</strong> <a href="/documentation/field-types/google-service-gmail/gmail-api-search-queries.html">Gmail API Search Queries</a></p><hr><h3 id="fetch-single-mail-gmail" tabindex="-1"><a href="/documentation/field-types/google-service-gmail/fetch-single-mail-gmail.html">Fetch Single Mail (Gmail)</a> <a class="header-anchor" href="#fetch-single-mail-gmail" aria-label="Permalink to “Fetch Single Mail (Gmail)”">​</a></h3><p>Retrieve a specific email from Gmail using message ID or search parameters.</p><p><strong>Features:</strong></p><ul><li>Optimized for single email retrieval</li><li>Search using Gmail query syntax</li><li>Support for retry mechanisms</li><li>Regex filtering for data extraction</li><li>Automatic mark as read functionality</li><li>Wait for expected emails</li></ul><hr><h3 id="send-mail-gmail" tabindex="-1"><a href="/documentation/field-types/google-service-gmail/send-mail-gmail.html">Send Mail (Gmail)</a> <a class="header-anchor" href="#send-mail-gmail" aria-label="Permalink to “Send Mail (Gmail)”">​</a></h3><p>Send emails through Gmail programmatically.</p><p><strong>Features:</strong></p><ul><li>Send emails with custom content</li><li>Support for multiple recipients</li><li>File attachments support</li><li>Custom file names and MIME types</li><li>HTML and plain text formats</li><li>Retry mechanism on failure</li><li>Variable support for dynamic content</li></ul><hr><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to “Key Features”">​</a></h2><ul><li><strong>Gmail API Integration</strong> - Direct connection to Gmail services</li><li><strong>Email Retrieval</strong> - Fetch emails based on various criteria</li><li><strong>Email Sending</strong> - Send emails programmatically</li><li><strong>Message Parsing</strong> - Extract data from email content</li><li><strong>Attachment Handling</strong> - Work with email attachments</li><li><strong>Advanced Search</strong> - Use Gmail&#39;s powerful search syntax</li><li><strong>Label Management</strong> - Filter by Gmail labels and categories</li><li><strong>Retry Mechanisms</strong> - Automatic retry on failure</li><li><strong>Regex Support</strong> - Extract data using regular expressions</li></ul><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to “Use Cases”">​</a></h2><ul><li><strong>Fetch verification codes from emails</strong> - Extract OTP codes for automation</li><li><strong>Monitor inbox for specific messages</strong> - Wait for confirmation emails</li><li><strong>Send automated email notifications</strong> - Trigger email sending based on events</li><li><strong>Extract data from email content for form filling</strong> - Parse structured data from emails</li><li><strong>Process email attachments in automation workflows</strong> - Download and process attachments</li></ul><h2 id="related-documentation" tabindex="-1">Related Documentation <a class="header-anchor" href="#related-documentation" aria-label="Permalink to “Related Documentation”">​</a></h2><ul><li><a href="/documentation/field-types/google-service-gmail/fetch-mail-gmail.html">Fetch Mail (Gmail)</a></li><li><a href="/documentation/field-types/google-service-gmail/gmail-api-search-queries.html">Gmail API Search Queries</a></li><li><a href="/documentation/form-fields/field-types.html">Field Types</a></li><li><a href="/documentation/variable.html">Variables</a></li><li><a href="https://developers.google.com/gmail/api" target="_blank" rel="noreferrer">Gmail API Documentation</a></li></ul>',27)])])}const f=a(r,[["render",o]]);export{h as __pageData,f as default};
