import{_ as t,c as a,o as i,aj as s}from"./chunks/framework.DRmbE9yB.js";const g=JSON.parse('{"title":"Fetch Mail (Gmail)","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/field-types/google-service-gmail/fetch-mail-gmail.md","filePath":"documentation/field-types/google-service-gmail/fetch-mail-gmail.md","lastUpdated":1768407692000}'),r={name:"documentation/field-types/google-service-gmail/fetch-mail-gmail.md"};function n(l,e,o,p,d,c){return i(),a("div",null,[...e[0]||(e[0]=[s(`<h1 id="fetch-mail-gmail" tabindex="-1">Fetch Mail (Gmail) <a class="header-anchor" href="#fetch-mail-gmail" aria-label="Permalink to “Fetch Mail (Gmail)”">​</a></h1><p>Retrieve multiple emails from Gmail based on search criteria and filters.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to “Overview”">​</a></h2><p>The Fetch Mail (Gmail) field type allows you to search and retrieve emails from your Gmail account using powerful search queries and filters. You can fetch specific parts of emails, filter by labels, apply regular expressions, and automatically mark emails as read.</p><h2 id="configuration-options" tabindex="-1">Configuration Options <a class="header-anchor" href="#configuration-options" aria-label="Permalink to “Configuration Options”">​</a></h2><table tabindex="0"><thead><tr><th>Option</th><th>Type</th><th>Description</th><th>Required</th></tr></thead><tbody><tr><td><a href="#google-service-account">Google Service Account</a></td><td>Dropdown</td><td>Select logged-in Google account</td><td><strong>Yes</strong></td></tr><tr><td><a href="#mail-parts">Mail Parts</a></td><td>Input Tags</td><td>Select which mail parts to fetch</td><td><strong>Yes</strong></td></tr><tr><td><a href="#enable-max-results">Enable Max Results</a></td><td>Switch</td><td>Limit number of results</td><td>No</td></tr><tr><td><a href="#max-results-count">Max Results Count</a></td><td>Input</td><td>Number of results to return</td><td>No</td></tr><tr><td><a href="#use-gmail-labels">Use Gmail Labels</a></td><td>Switch</td><td>Search in specific Gmail labels</td><td>No</td></tr><tr><td><a href="#gmail-label-ids">Gmail Label IDs</a></td><td>Dropdown Multiple</td><td>Select Gmail labels to search</td><td>No</td></tr><tr><td><a href="#include-spam-trash">Include Spam/Trash</a></td><td>Switch</td><td>Include spam and trash in results</td><td>No</td></tr><tr><td><a href="#enable-retry-on-failure">Enable Retry on Failure</a></td><td>Switch</td><td>Retry if request fails</td><td>No</td></tr><tr><td><a href="#max-retries-failure">Max Retries (Failure)</a></td><td>Input</td><td>Maximum retry attempts on failure</td><td>No</td></tr><tr><td><a href="#retry-until-mail-received">Retry Until Mail Received</a></td><td>Switch</td><td>Keep retrying until mail is found</td><td>No</td></tr><tr><td><a href="#max-retries-until-received">Max Retries (Until Received)</a></td><td>Input</td><td>Maximum retry attempts until mail received</td><td>No</td></tr><tr><td><a href="#timeout">Timeout</a></td><td>Input</td><td>Timeout duration in seconds</td><td>No</td></tr><tr><td><a href="#enable-regex-filter">Enable Regex Filter</a></td><td>Switch</td><td>Filter data with regular expression</td><td>No</td></tr><tr><td><a href="#regular-expression">Regular Expression</a></td><td>Input</td><td>Regex pattern to match</td><td>No</td></tr><tr><td><a href="#regex-replace">Regex Replace</a></td><td>Input</td><td>Replacement string for regex matches</td><td>No</td></tr><tr><td><a href="#remove-unread-label">Remove UNREAD Label</a></td><td>Switch</td><td>Mark email as read after fetching</td><td>No</td></tr><tr><td><a href="#search-query">Search Query</a></td><td>Default Value</td><td>Gmail search query</td><td>No</td></tr></tbody></table><hr><h2 id="option-details" tabindex="-1">Option Details <a class="header-anchor" href="#option-details" aria-label="Permalink to “Option Details”">​</a></h2><h3 id="google-service-account" tabindex="-1">Google Service Account <a class="header-anchor" href="#google-service-account" aria-label="Permalink to “Google Service Account”">​</a></h3><p>Select the Google account you want to use for accessing Gmail.</p><p><strong>Type:</strong> Dropdown</p><p><strong>Description:</strong> Choose from your logged-in Google accounts. Make sure the selected account has Gmail access enabled.</p><p><strong>Example:</strong></p><ul><li><code>user@gmail.com</code></li><li><code>work@company.com</code></li></ul><hr><h3 id="mail-parts" tabindex="-1">Mail Parts <a class="header-anchor" href="#mail-parts" aria-label="Permalink to “Mail Parts”">​</a></h3><p>Select which parts of the email you want to retrieve.</p><p><strong>Type:</strong> Input Tags</p><p><strong>Available Parts:</strong></p><ul><li><code>body</code> - Email body content</li><li><code>subject</code> - Email subject line</li><li><code>from</code> - Sender email address</li><li><code>snippet</code> - Short preview of email content</li><li><code>to</code> - Recipient email address(es)</li></ul><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>body, subject, from</span></span>
<span class="line"><span>subject, snippet</span></span>
<span class="line"><span>body, from, to</span></span></code></pre></div><hr><h3 id="enable-max-results" tabindex="-1">Enable Max Results <a class="header-anchor" href="#enable-max-results" aria-label="Permalink to “Enable Max Results”">​</a></h3><p>Enable limiting the number of results returned.</p><p><strong>Type:</strong> Switch</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Limit number of results</li><li><strong>OFF</strong> - Return all matching results</li></ul><hr><h3 id="max-results-count" tabindex="-1">Max Results Count <a class="header-anchor" href="#max-results-count" aria-label="Permalink to “Max Results Count”">​</a></h3><p>Specify the maximum number of emails to return.</p><p><strong>Type:</strong> Input</p><p><strong>Description:</strong> Set how many emails should be fetched (Return Max Results).</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>1    (Return only 1 email)</span></span>
<span class="line"><span>5    (Return up to 5 emails)</span></span>
<span class="line"><span>10   (Return up to 10 emails)</span></span>
<span class="line"><span>50   (Return up to 50 emails)</span></span></code></pre></div><p><strong>Note:</strong> This option only works when <a href="#enable-max-results">Enable Max Results</a> is turned ON.</p><hr><h3 id="use-gmail-labels" tabindex="-1">Use Gmail Labels <a class="header-anchor" href="#use-gmail-labels" aria-label="Permalink to “Use Gmail Labels”">​</a></h3><p>Search emails in specific Gmail labels.</p><p><strong>Type:</strong> Switch</p><p><strong>Description:</strong> Enable this to filter emails by Gmail labels.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Search in selected labels only</li><li><strong>OFF</strong> - Search in all emails</li></ul><hr><h3 id="gmail-label-ids" tabindex="-1">Gmail Label IDs <a class="header-anchor" href="#gmail-label-ids" aria-label="Permalink to “Gmail Label IDs”">​</a></h3><p>Select which Gmail labels to search in.</p><p><strong>Type:</strong> Dropdown Multiple</p><p><strong>Available Labels:</strong></p><ul><li><code>INBOX</code> - Inbox emails</li><li><code>SPAM</code> - Spam folder</li><li><code>TRASH</code> - Trash/Deleted emails</li><li><code>UNREAD</code> - Unread emails</li><li><code>STARRED</code> - Starred/Important emails</li><li><code>SENT</code> - Sent emails</li><li><code>IMPORTANT</code> - Important emails</li><li><code>DRAFT</code> - Draft emails</li></ul><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>INBOX, UNREAD</span></span>
<span class="line"><span>STARRED, IMPORTANT</span></span>
<span class="line"><span>SENT</span></span></code></pre></div><p><strong>Note:</strong> This option only works when <a href="#use-gmail-labels">Use Gmail Labels</a> is turned ON.</p><hr><h3 id="include-spam-trash" tabindex="-1">Include Spam/Trash <a class="header-anchor" href="#include-spam-trash" aria-label="Permalink to “Include Spam/Trash”">​</a></h3><p>Include emails from Spam and Trash folders in the results.</p><p><strong>Type:</strong> Switch</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Include spam and trash</li><li><strong>OFF</strong> - Exclude spam and trash (default)</li></ul><hr><h3 id="enable-retry-on-failure" tabindex="-1">Enable Retry on Failure <a class="header-anchor" href="#enable-retry-on-failure" aria-label="Permalink to “Enable Retry on Failure”">​</a></h3><p>Automatically retry the request if it fails.</p><p><strong>Type:</strong> Switch</p><p><strong>Description:</strong> When enabled, the system will retry fetching emails if an error occurs.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Enable retry on failure</li><li><strong>OFF</strong> - Don&#39;t retry on failure</li></ul><p><strong>Use Cases:</strong></p><ul><li>Network connectivity issues</li><li>Temporary API errors</li><li>Rate limiting scenarios</li></ul><hr><h3 id="max-retries-failure" tabindex="-1">Max Retries (Failure) <a class="header-anchor" href="#max-retries-failure" aria-label="Permalink to “Max Retries (Failure)”">​</a></h3><p>Maximum number of retry attempts if the request fails.</p><p><strong>Type:</strong> Input</p><p><strong>Description:</strong> Specify how many times the system should retry before giving up.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>3    (Retry up to 3 times)</span></span>
<span class="line"><span>5    (Retry up to 5 times)</span></span>
<span class="line"><span>10   (Retry up to 10 times)</span></span></code></pre></div><p><strong>Note:</strong> This option only works when <a href="#enable-retry-on-failure">Enable Retry on Failure</a> is turned ON.</p><hr><h3 id="retry-until-mail-received" tabindex="-1">Retry Until Mail Received <a class="header-anchor" href="#retry-until-mail-received" aria-label="Permalink to “Retry Until Mail Received”">​</a></h3><p>Keep retrying until the expected email is received.</p><p><strong>Type:</strong> Switch</p><p><strong>Description:</strong> Useful for waiting for verification emails, OTPs, or confirmation messages.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Retry until mail is found</li><li><strong>OFF</strong> - Don&#39;t retry if mail not found</li></ul><p><strong>Use Cases:</strong></p><ul><li>Waiting for verification codes</li><li>Expecting confirmation emails</li><li>Polling for specific messages</li></ul><hr><h3 id="max-retries-until-received" tabindex="-1">Max Retries (Until Received) <a class="header-anchor" href="#max-retries-until-received" aria-label="Permalink to “Max Retries (Until Received)”">​</a></h3><p>Maximum number of retry attempts when waiting for mail.</p><p><strong>Type:</strong> Input</p><p><strong>Description:</strong> Specify how many times to retry before giving up.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>10   (Retry up to 10 times)</span></span>
<span class="line"><span>20   (Retry up to 20 times)</span></span>
<span class="line"><span>50   (Retry up to 50 times)</span></span></code></pre></div><p><strong>Note:</strong> This option only works when <a href="#retry-until-mail-received">Retry Until Mail Received</a> is turned ON.</p><hr><h3 id="timeout" tabindex="-1">Timeout <a class="header-anchor" href="#timeout" aria-label="Permalink to “Timeout”">​</a></h3><p>Timeout duration in seconds between retry attempts.</p><p><strong>Type:</strong> Input</p><p><strong>Description:</strong> Specify how long to wait between each retry attempt.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>5     (Wait 5 seconds between retries)</span></span>
<span class="line"><span>10    (Wait 10 seconds between retries)</span></span>
<span class="line"><span>30    (Wait 30 seconds between retries)</span></span></code></pre></div><p><strong>Note:</strong> This option only works when <a href="#retry-until-mail-received">Retry Until Mail Received</a> is turned ON.</p><hr><h3 id="enable-regex-filter" tabindex="-1">Enable Regex Filter <a class="header-anchor" href="#enable-regex-filter" aria-label="Permalink to “Enable Regex Filter”">​</a></h3><p>Filter email data using regular expressions.</p><p><strong>Type:</strong> Switch</p><p><strong>Description:</strong> Apply regex patterns to extract or transform email content.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Enable regex filtering</li><li><strong>OFF</strong> - Return raw email data</li></ul><hr><h3 id="regular-expression" tabindex="-1">Regular Expression <a class="header-anchor" href="#regular-expression" aria-label="Permalink to “Regular Expression”">​</a></h3><p>Regular expression pattern to match in email content.</p><p><strong>Type:</strong> Input</p><p><strong>Description:</strong> Define a regex pattern to extract specific data from emails.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>\\d{6}                    (Match 6-digit codes)</span></span>
<span class="line"><span>[A-Z0-9]{8}              (Match 8-character alphanumeric codes)</span></span>
<span class="line"><span>verification code: (\\d+) (Extract verification code)</span></span></code></pre></div><p><strong>Note:</strong> This option only works when <a href="#enable-regex-filter">Enable Regex Filter</a> is turned ON.</p><hr><h3 id="regex-replace" tabindex="-1">Regex Replace <a class="header-anchor" href="#regex-replace" aria-label="Permalink to “Regex Replace”">​</a></h3><p>Replacement string for regex matches.</p><p><strong>Type:</strong> Input</p><p><strong>Description:</strong> Define what to replace matched patterns with. Use <code>$1</code>, <code>$2</code> for capture groups.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>$1                (Use first capture group)</span></span>
<span class="line"><span>Code: $1          (Prefix with &quot;Code: &quot;)</span></span>
<span class="line"><span>$1-$2             (Combine capture groups)</span></span></code></pre></div><p><strong>Note:</strong> This option only works when <a href="#enable-regex-filter">Enable Regex Filter</a> is turned ON.</p><hr><h3 id="remove-unread-label" tabindex="-1">Remove UNREAD Label <a class="header-anchor" href="#remove-unread-label" aria-label="Permalink to “Remove UNREAD Label”">​</a></h3><p>Automatically mark email as read after fetching.</p><p><strong>Type:</strong> Switch</p><p><strong>Description:</strong> Remove the UNREAD label from emails after they are retrieved.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Mark as read after fetching</li><li><strong>OFF</strong> - Keep email as unread</li></ul><p><strong>Use Cases:</strong></p><ul><li>Prevent duplicate processing</li><li>Clean up inbox automatically</li><li>Track processed emails</li></ul><hr><h3 id="search-query" tabindex="-1">Search Query <a class="header-anchor" href="#search-query" aria-label="Permalink to “Search Query”">​</a></h3><p>Gmail search query to filter emails.</p><p><strong>Type:</strong> Default Value Field</p><p><strong>Description:</strong> Use Gmail&#39;s powerful search syntax to find specific emails.</p><div class="tip custom-block"><p class="custom-block-title">Set Query in Default Value</p><p>You can set the search query in the <strong>&quot;If excel column value is empty then fill this default value&quot;</strong> field option.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>from:noreply@example.com subject:verification</span></span>
<span class="line"><span>is:unread after:2024/01/01</span></span>
<span class="line"><span>has:attachment larger:5M</span></span></code></pre></div></div><p><strong>Learn More:</strong> See <a href="/documentation/field-types/google-service-gmail/gmail-api-search-queries.html">Gmail API Search Queries</a> for detailed search syntax and examples.</p><hr><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to “Use Cases”">​</a></h2><ul><li><strong>Fetch verification codes from emails</strong> - Extract OTP codes for automation</li><li><strong>Monitor inbox for specific messages</strong> - Wait for confirmation emails</li><li><strong>Extract data from email content</strong> - Parse structured data from emails</li><li><strong>Process email attachments</strong> - Retrieve emails with specific attachments</li><li><strong>Automate email-based workflows</strong> - Trigger actions based on received emails</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to “Best Practices”">​</a></h2><h3 id="✅-do-s" tabindex="-1">✅ Do&#39;s <a class="header-anchor" href="#✅-do-s" aria-label="Permalink to “✅ Do&#39;s”">​</a></h3><ul><li><strong>Use specific search queries</strong> - Narrow down results with precise filters</li><li><strong>Enable retry for verification emails</strong> - Use retry mechanism for expected emails</li><li><strong>Use regex for data extraction</strong> - Extract specific patterns like codes or URLs</li><li><strong>Mark emails as read</strong> - Prevent duplicate processing</li><li><strong>Limit max results</strong> - Improve performance by fetching only needed emails</li></ul><h3 id="❌-don-ts" tabindex="-1">❌ Don&#39;ts <a class="header-anchor" href="#❌-don-ts" aria-label="Permalink to “❌ Don&#39;ts”">​</a></h3><ul><li><strong>Don&#39;t fetch all emails</strong> - Use search queries to filter results</li><li><strong>Don&#39;t set excessive retries</strong> - Balance between reliability and performance</li><li><strong>Don&#39;t ignore timeout settings</strong> - Set appropriate timeout for retry scenarios</li><li><strong>Don&#39;t process spam unnecessarily</strong> - Exclude spam unless specifically needed</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to “Troubleshooting”">​</a></h2><h3 id="no-emails-found" tabindex="-1">No Emails Found <a class="header-anchor" href="#no-emails-found" aria-label="Permalink to “No Emails Found”">​</a></h3><p><strong>Solution:</strong> Verify your search query is correct and emails matching the criteria exist.</p><h3 id="permission-denied" tabindex="-1">Permission Denied <a class="header-anchor" href="#permission-denied" aria-label="Permalink to “Permission Denied”">​</a></h3><p><strong>Solution:</strong> Ensure the selected Google account has Gmail API access enabled.</p><h3 id="timeout-errors" tabindex="-1">Timeout Errors <a class="header-anchor" href="#timeout-errors" aria-label="Permalink to “Timeout Errors”">​</a></h3><p><strong>Solution:</strong> Increase timeout duration or reduce max retry attempts.</p><h3 id="regex-not-working" tabindex="-1">Regex Not Working <a class="header-anchor" href="#regex-not-working" aria-label="Permalink to “Regex Not Working”">​</a></h3><p><strong>Solution:</strong> Test your regex pattern and ensure it matches the email content format.</p><h3 id="too-many-results" tabindex="-1">Too Many Results <a class="header-anchor" href="#too-many-results" aria-label="Permalink to “Too Many Results”">​</a></h3><p><strong>Solution:</strong> Enable max results limit and refine your search query.</p><h2 id="related-documentation" tabindex="-1">Related Documentation <a class="header-anchor" href="#related-documentation" aria-label="Permalink to “Related Documentation”">​</a></h2><ul><li><a href="/documentation/field-types/google-service-gmail/gmail-api-search-queries.html">Gmail API Search Queries</a></li><li><a href="/documentation/field-types/google-service-gmail.html">Google Service - Gmail</a></li><li><a href="/documentation/form-fields/field-types.html">Field Types</a></li><li><a href="/documentation/variable.html">Variables</a></li><li><a href="https://developers.google.com/gmail/api" target="_blank" rel="noreferrer">Gmail API Documentation</a></li></ul>`,160)])])}const u=t(r,[["render",n]]);export{g as __pageData,u as default};
