import{_ as t,c as o,o as a,aj as s}from"./chunks/framework.DRmbE9yB.js";const g=JSON.parse('{"title":"Program Helper - Field Types","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/field-types/program-helper.md","filePath":"documentation/field-types/program-helper.md","lastUpdated":1758109585000}'),i={name:"documentation/field-types/program-helper.md"};function l(n,e,r,d,c,p){return a(),o("div",null,[...e[0]||(e[0]=[s(`<h1 id="program-helper-field-types" tabindex="-1">Program Helper - Field Types <a class="header-anchor" href="#program-helper-field-types" aria-label="Permalink to ‚ÄúProgram Helper - Field Types‚Äù">‚Äã</a></h1><p>Program Helper field types allow you to interact with external applications, system functions, and perform advanced automation tasks beyond web browser capabilities. These field types require a helper program to be installed on your operating system.</p><div class="warning custom-block"><p class="custom-block-title">Important</p><p>To use these fields, you will first have to install the helper program in the OS, only after that you can do this field work.</p></div><h2 id="install-helper-program" tabindex="-1">üì• Install Helper Program <a class="header-anchor" href="#install-helper-program" aria-label="Permalink to ‚Äúüì• Install Helper Program‚Äù">‚Äã</a></h2><p>The <a href="/documentation/program.html#download-and-install"><strong>Helper Program</strong></a> is required to enable system-level automation and external application interactions. Download and install the appropriate version for your operating system:</p><h2 id="x-focus-application-window" tabindex="-1">üñ•Ô∏è X-Focus Application Window <a class="header-anchor" href="#x-focus-application-window" aria-label="Permalink to ‚Äúüñ•Ô∏è X-Focus Application Window‚Äù">‚Äã</a></h2><p>Focus on a specific application window to bring it to the foreground.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>Application executable name or window title</td><td><code>ahk_exe chrome.exe</code></td></tr></tbody></table><p><strong>Usage Examples:</strong></p><ul><li><code>ahk_exe chrome.exe</code> - Focus Chrome browser</li><li><code>ahk_exe notepad.exe</code> - Focus Notepad</li><li><code>Untitled - Notepad</code> - Focus window by title</li></ul><h2 id="x-click-space-key" tabindex="-1">‚å®Ô∏è X-Click (Space Key) <a class="header-anchor" href="#x-click-space-key" aria-label="Permalink to ‚Äú‚å®Ô∏è X-Click (Space Key)‚Äù">‚Äã</a></h2><p>Simulate a space key press on the currently focused element.</p><p><strong>Usage:</strong></p><ul><li>No additional configuration required</li><li>Automatically sends a space key press to the focused element</li><li>Useful for activating buttons or toggling checkboxes</li></ul><h2 id="x-paste-text" tabindex="-1">üìù X-Paste Text <a class="header-anchor" href="#x-paste-text" aria-label="Permalink to ‚Äúüìù X-Paste Text‚Äù">‚Äã</a></h2><p>Paste text content into the currently focused element.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>Text content to paste</td></tr><tr><td><strong>Clear field value then fill new value</strong></td><td>Clear existing content before pasting</td></tr></tbody></table><p><strong>Usage Examples:</strong></p><ul><li><strong>Field Value</strong>: <code>Hello World</code> - Pastes &quot;Hello World&quot;</li><li><strong>Field Value</strong>: <code>{$variable$}</code> - Pastes dynamic variable content</li></ul><h2 id="x-winalwaysontop-activate" tabindex="-1">üìå X-WinAlwaysOnTop (Activate) <a class="header-anchor" href="#x-winalwaysontop-activate" aria-label="Permalink to ‚Äúüìå X-WinAlwaysOnTop (Activate)‚Äù">‚Äã</a></h2><p>Set the current window to always stay on top of other windows.</p><p><strong>Usage:</strong></p><ul><li>No additional configuration required</li><li>Makes the current window stay above all other windows</li><li>Useful for keeping important dialogs visible</li></ul><h2 id="x-winalwaysontop-deactivate" tabindex="-1">üìå X-WinAlwaysOnTop (Deactivate) <a class="header-anchor" href="#x-winalwaysontop-deactivate" aria-label="Permalink to ‚Äúüìå X-WinAlwaysOnTop (Deactivate)‚Äù">‚Äã</a></h2><p>Remove the always-on-top property from the current window.</p><p><strong>Usage:</strong></p><ul><li>No additional configuration required</li><li>Returns the window to normal layering behavior</li><li>Use after completing tasks that required always-on-top</li></ul><h2 id="x-keywait" tabindex="-1">‚è≥ X-KeyWait <a class="header-anchor" href="#x-keywait" aria-label="Permalink to ‚Äú‚è≥ X-KeyWait‚Äù">‚Äã</a></h2><p>Wait for a specific key to be released before proceeding.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>Key to wait for</td><td><code>a</code>, <code>{Enter}</code>, <code>{Space}</code></td></tr></tbody></table><p><strong>Usage Examples:</strong></p><ul><li><strong>Field Value</strong>: <code>a</code> - Waits for &#39;a&#39; key to be released</li><li><strong>Field Value</strong>: <code>{Enter}</code> - Waits for Enter key to be released</li><li><strong>Field Value</strong>: <code>{Space}</code> - Waits for Space key to be released</li></ul><div class="info custom-block"><p class="custom-block-title">Multiple Keystrokes</p><p>If you need to send multiple keystrokes, like <code>&quot;alt+a&quot;</code>, you will need to insert two <code>X-KeyWait</code> field types in the extension form: one for <code>&quot;alt&quot;</code> and one for <code>&quot;a&quot;</code>.</p></div><h2 id="x-send" tabindex="-1">‚å®Ô∏è X-Send <a class="header-anchor" href="#x-send" aria-label="Permalink to ‚Äú‚å®Ô∏è X-Send‚Äù">‚Äã</a></h2><p>Send keystrokes, special keys, or text to the currently focused element.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>Keystrokes, special keys, or text to send</td><td><code>{F1}</code>, <code>^s</code>, <code>Hello World</code></td></tr><tr><td><strong>Clear field value then fill new value</strong></td><td>Clear existing content before sending</td><td></td></tr></tbody></table><p><strong>Special Key Codes:</strong></p><ul><li><code>{F1}</code> - Function key F1</li><li><code>^s</code> - Ctrl+S (save)</li><li><code>^c</code> - Ctrl+C (copy)</li><li><code>^v</code> - Ctrl+V (paste)</li><li><code>{Enter}</code> - Enter key</li><li><code>{Tab}</code> - Tab key</li><li><code>{Esc}</code> - Escape key</li></ul><p><strong>Usage Examples:</strong></p><ul><li><strong>Field Value</strong>: <code>{F1}</code> - Sends F1 key</li><li><strong>Field Value</strong>: <code>^s</code> - Sends Ctrl+S (save)</li><li><strong>Field Value</strong>: <code>Hello World</code> - Types &quot;Hello World&quot;</li></ul><h2 id="x-mouseclick" tabindex="-1">üñ±Ô∏è X-MouseClick <a class="header-anchor" href="#x-mouseclick" aria-label="Permalink to ‚Äúüñ±Ô∏è X-MouseClick‚Äù">‚Äã</a></h2><p>Simulate mouse clicks at specific coordinates or on the current cursor position.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>Click parameters</td><td><code>left,,,1</code></td></tr></tbody></table><p><strong>Click Parameters Format:</strong></p><ul><li><code>Button, X, Y, ClickCount</code></li><li><strong>Button</strong>: <code>left</code>, <code>right</code>, <code>middle</code></li><li><strong>X, Y</strong>: Coordinates (leave empty for current position)</li><li><strong>ClickCount</strong>: Number of clicks (1 = single click, 2 = double click)</li></ul><p><strong>Usage Examples:</strong></p><ul><li><code>left,,,1</code> - Left click once at current position</li><li><code>right,100,200,1</code> - Right click at coordinates (100, 200)</li><li><code>left,,,2</code> - Double left click at current position</li></ul><h2 id="x-controlclick" tabindex="-1">üéØ X-ControlClick <a class="header-anchor" href="#x-controlclick" aria-label="Permalink to ‚ÄúüéØ X-ControlClick‚Äù">‚Äã</a></h2><p>Click on specific control elements within applications.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>Control click parameters</td><td><code>x100 y200,Untitled - Notepad</code></td></tr></tbody></table><p><strong>Control Click Parameters Format:</strong></p><ul><li><code>Coordinates, WindowTitle</code></li><li><strong>Coordinates</strong>: <code>x100 y200</code> (pixel coordinates)</li><li><strong>WindowTitle</strong>: Target window title</li></ul><p><strong>Usage Examples:</strong></p><ul><li><code>x100 y200,Untitled - Notepad</code> - Click at (100, 200) in Notepad window</li><li><code>x50 y50,Calculator</code> - Click at (50, 50) in Calculator window</li></ul><h2 id="x-imagesearch" tabindex="-1">üîç X-ImageSearch <a class="header-anchor" href="#x-imagesearch" aria-label="Permalink to ‚Äúüîç X-ImageSearch‚Äù">‚Äã</a></h2><p>Search for images on the screen and perform actions based on their location.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>Image search parameters</td></tr><tr><td><strong>Image Path</strong></td><td>Path to the image file to search for</td></tr></tbody></table><p><strong>Usage:</strong></p><ul><li>Upload or specify an image file to search for</li><li>The system will locate the image on screen</li><li>Perform actions based on the found coordinates</li></ul><hr><h2 id="fileexist" tabindex="-1">üìÇ FileExist <a class="header-anchor" href="#fileexist" aria-label="Permalink to ‚ÄúüìÇ FileExist‚Äù">‚Äã</a></h2><p>Check for the existence of a file or folder on your PC using file patterns and wildcards.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>File pattern to search for (path + pattern)</td><td><code>C:\\Temp\\*.tmp</code></td></tr></tbody></table><p><strong>Set in:</strong> Field Default Value or Excel Field Column</p><p><strong>File Pattern Examples:</strong></p><table tabindex="0"><thead><tr><th>Pattern</th><th>Description</th><th>Matches</th></tr></thead><tbody><tr><td><code>*.*</code> or <code>*</code></td><td>All files</td><td>Any file with or without extension</td></tr><tr><td><code>*.htm</code></td><td>Files with .htm extension</td><td><code>index.htm</code>, <code>page.html</code>, <code>file.htm</code></td></tr><tr><td><code>*.</code></td><td>Files without extension</td><td><code>README</code>, <code>LICENSE</code>, <code>Makefile</code></td></tr><tr><td><code>log?.txt</code></td><td>Single character wildcard</td><td><code>log1.txt</code>, <code>log2.txt</code> (not <code>log10.txt</code>)</td></tr><tr><td><code>*report*</code></td><td>Contains word &quot;report&quot;</td><td><code>report.pdf</code>, <code>monthly_report.xlsx</code>, <code>report_2024.doc</code></td></tr></tbody></table><p><strong>Wildcard Characters:</strong></p><ul><li><code>*</code> - Matches zero or more characters</li><li><code>?</code> - Matches exactly one character</li><li><code>*.ext</code> - Matches all files with specific extension</li><li><code>prefix*</code> - Matches files starting with prefix</li><li><code>*suffix</code> - Matches files ending with suffix</li></ul><p><strong>Usage Examples:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># Check for temporary files</span></span>
<span class="line"><span>C:\\Temp\\*.tmp</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Check for log files with single digit</span></span>
<span class="line"><span>C:\\Logs\\log?.txt</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Check for any report files</span></span>
<span class="line"><span>C:\\Documents\\*report*</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Check for all files in directory</span></span>
<span class="line"><span>C:\\Users\\Public\\*.*</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Check for files without extension</span></span>
<span class="line"><span>C:\\Scripts\\*.</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Check for specific file</span></span>
<span class="line"><span>C:\\Users\\Username\\Desktop\\file.txt</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Using variable</span></span>
<span class="line"><span>{$filePath$}\\*.pdf</span></span></code></pre></div><p><strong>Return Value:</strong></p><ul><li>Returns <code>1</code> (true) if file/folder exists</li><li>Returns <code>0</code> (false) if file/folder does not exist</li></ul><p><strong>Use Cases:</strong></p><ul><li>Verify file downloads completed</li><li>Check if required files exist before processing</li><li>Validate folder structure</li><li>Monitor file creation</li><li>Conditional logic based on file existence</li></ul><p><strong>Example Workflow:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>1. Set Field Value: C:\\Downloads\\*.pdf</span></span>
<span class="line"><span>2. FileExist checks for any PDF files</span></span>
<span class="line"><span>3. If found (returns 1), proceed with next action</span></span>
<span class="line"><span>4. If not found (returns 0), skip or show error</span></span></code></pre></div><div class="tip custom-block"><p class="custom-block-title">Pattern Matching</p><p>Use wildcards to check for multiple files at once. For example, <code>C:\\Logs\\*.log</code> will return true if any .log file exists in the Logs directory.</p></div><div class="info custom-block"><p class="custom-block-title">Case Sensitivity</p><p>File patterns on Windows are case-insensitive. <code>*.TXT</code> and <code>*.txt</code> will match the same files.</p></div><hr><h2 id="x-filegetsize" tabindex="-1">üìÅ X-FileGetSize <a class="header-anchor" href="#x-filegetsize" aria-label="Permalink to ‚ÄúüìÅ X-FileGetSize‚Äù">‚Äã</a></h2><p>Get the file size in bytes for local files or remote URLs.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>File path or URL</td><td><code>C:\\Users\\P1\\Pictures\\photo.jpg</code></td></tr></tbody></table><p><strong>Supported Paths:</strong></p><ul><li><strong>Local files</strong>: <code>C:\\Users\\P1\\Pictures\\photo.jpg</code></li><li><strong>Remote URLs</strong>: <code>https://images.pexels.com/photos/674010/pexels-photo-674010.jpeg</code></li></ul><p><strong>Usage Examples:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span># Local File</span></span>
<span class="line"><span>C:\\Users\\P1\\Pictures\\photo.jpg</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Remote URL</span></span>
<span class="line"><span>https://images.pexels.com/photos/674010/pexels-photo-674010.jpeg</span></span></code></pre></div><h2 id="x-fileextension" tabindex="-1">üìÑ X-FileExtension <a class="header-anchor" href="#x-fileextension" aria-label="Permalink to ‚ÄúüìÑ X-FileExtension‚Äù">‚Äã</a></h2><p>Get the file extension from a file path or URL.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th><th>Example</th></tr></thead><tbody><tr><td><strong>Field Value</strong></td><td>File path or URL</td><td><code>image.jpg</code>, <code>script.js</code></td></tr></tbody></table><p><strong>Supported Extensions:</strong></p><ul><li><strong>Images</strong>: <code>png</code>, <code>jpg</code>, <code>jpeg</code>, <code>gif</code>, <code>bmp</code></li><li><strong>Documents</strong>: <code>pdf</code>, <code>doc</code>, <code>docx</code>, <code>txt</code></li><li><strong>Code</strong>: <code>js</code>, <code>html</code>, <code>css</code>, <code>py</code>, <code>java</code></li><li><strong>Archives</strong>: <code>zip</code>, <code>rar</code>, <code>7z</code></li></ul><p><strong>Usage Examples:</strong></p><ul><li><strong>Input</strong>: <code>photo.jpg</code> ‚Üí <strong>Output</strong>: <code>jpg</code></li><li><strong>Input</strong>: <code>script.js</code> ‚Üí <strong>Output</strong>: <code>js</code></li><li><strong>Input</strong>: <code>document.pdf</code> ‚Üí <strong>Output</strong>: <code>pdf</code></li></ul>`,93)])])}const u=t(i,[["render",l]]);export{g as __pageData,u as default};
