import{_ as e,c as i,o as r,ah as o,aD as a}from"./chunks/framework.Dp_r5Pqh.js";const f=JSON.parse('{"title":"Save as PDF","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/field-types/save-as-pdf.md","filePath":"documentation/field-types/save-as-pdf.md","lastUpdated":1751799890000}'),n={name:"documentation/field-types/save-as-pdf.md"};function s(l,t,d,g,p,h){return r(),i("div",null,[...t[0]||(t[0]=[o('<h1 id="save-as-pdf" tabindex="-1">Save as PDF <a class="header-anchor" href="#save-as-pdf" aria-label="Permalink to “Save as PDF”">​</a></h1><p>Save web pages as PDF files using the browser&#39;s print functionality. Configure PDF generation settings, file naming, and save options for automated document creation.</p><h2 id="save-as-pdf-field-options" tabindex="-1">📋 Field Options <a class="header-anchor" href="#save-as-pdf-field-options" aria-label="Permalink to “📋 Field Options”">​</a></h2><table tabindex="0"><thead><tr><th>Field Options</th><th>Required</th><th>Description</th><th>Example Values</th></tr></thead><tbody><tr><td><strong>Press Ctrl + P</strong></td><td>No</td><td>Automatically trigger print dialog</td><td>Toggle ON/OFF</td></tr><tr><td><strong>Press Ctrl + S</strong></td><td>No</td><td>Use Ctrl+S shortcut for saving</td><td>Toggle ON/OFF</td></tr><tr><td><strong>Do not identify print window as per image</strong></td><td>No</td><td>Skip print window detection</td><td>Toggle ON/OFF</td></tr><tr><td><strong>Set Image Path to Identify Print Window</strong></td><td>No</td><td>Custom image for print window detection</td><td><code>/path/to/image.png</code></td></tr><tr><td><strong>Set delay time after opening print window</strong></td><td>No</td><td>Wait time after print dialog opens</td><td><code>1000</code>, <code>2000</code> (milliseconds)</td></tr><tr><td><strong>If file already exists in the save location then replace it</strong></td><td>No</td><td>Overwrite existing files with same name</td><td>Toggle ON/OFF</td></tr><tr><td><strong>Set the <code>confirm replace save as browser window</code> title name</strong></td><td>No</td><td>Set the confirm replace save as browser window title name</td><td>Toggle ON/OFF</td></tr><tr><td><strong>Set the <code>save as browser window</code> title name</strong></td><td>No</td><td>Set the save as browser window title name</td><td>Toggle ON/OFF</td></tr><tr><td><strong>Direct Save Page to PDF (Firefox only)</strong></td><td>No</td><td>Direct PDF save without print dialog</td><td>Toggle ON/OFF</td></tr><tr><td><strong>Field Value</strong></td><td>No</td><td>Custom filename for PDF</td><td><code>document.pdf</code>, <code>report_{$name$}.pdf</code></td></tr></tbody></table><h2 id="save-as-pdf-configuration" tabindex="-1">⚙️ Configuration <a class="header-anchor" href="#save-as-pdf-configuration" aria-label="Permalink to “⚙️ Configuration”">​</a></h2><h3 id="print-options" tabindex="-1">🖨️ Print Options <a class="header-anchor" href="#print-options" aria-label="Permalink to “🖨️ Print Options”">​</a></h3><h4 id="press-ctrl-p" tabindex="-1">📌 Press Ctrl + P <a class="header-anchor" href="#press-ctrl-p" aria-label="Permalink to “📌 Press Ctrl + P”">​</a></h4><p><strong>Purpose:</strong> Automatically trigger the print dialog without manual keyboard input.</p><p><strong>How it works:</strong></p><ul><li>If you want to save the page through <code>Ctrl + P</code>, turn on this option</li><li>No need to manually press <code>Ctrl + P</code> buttons</li></ul><div class="info custom-block"><p class="custom-block-title">Note</p><p>If your file/PDF is downloaded after clicking any button, provide the selector query of that button in field selector query.</p></div><hr><h4 id="do-not-identify-print-window-as-per-image" tabindex="-1">📌 Do not identify print window as per image <a class="header-anchor" href="#do-not-identify-print-window-as-per-image" aria-label="Permalink to “📌 Do not identify print window as per image”">​</a></h4><p><strong>Purpose:</strong> Skip automatic detection of print preview window.</p><p><strong>When to use:</strong></p><ul><li>When print window detection causes issues</li><li>For faster execution without image matching</li><li>When print preview appears differently</li></ul><hr><h4 id="set-image-path-to-identify-print-window" tabindex="-1">📌 Set Image Path to Identify Print Window <a class="header-anchor" href="#set-image-path-to-identify-print-window" aria-label="Permalink to “📌 Set Image Path to Identify Print Window”">​</a></h4><p><strong>Purpose:</strong> Use custom image to detect when print preview window is ready.</p><p><strong>How to configure:</strong></p><ol><li><strong>Save an image</strong> of the print preview window on your PC</li><li><strong>Provide the image path</strong> in this option</li><li><strong>Extension waits</strong> until the image is visible on screen</li></ol><p><strong>Image Requirements:</strong></p><ul><li>Clear screenshot of print preview window</li><li>Unique elements that identify the print dialog</li><li>Reasonable file size for quick processing</li></ul><p><strong>Example Image:</strong></p><img src="'+a+'" alt="Print Window Detection"><div class="warning custom-block"><p class="custom-block-title">Important</p><ul><li>If you are using the same image to other pc so maybe the image is not working properly.</li><li>So you can take the image from the same pc where you are using the extension.</li><li>If you resize the browser window so maybe the image is not working properly.</li></ul></div><hr><h4 id="set-delay-time-after-opening-print-window" tabindex="-1">📌 Set delay time after opening print window <a class="header-anchor" href="#set-delay-time-after-opening-print-window" aria-label="Permalink to “📌 Set delay time after opening print window”">​</a></h4><p><strong>Purpose:</strong> Add wait time between opening print window and saving the PDF.</p><p><strong>Configuration:</strong></p><ul><li><strong>Delay time</strong> in milliseconds</li><li><strong>Recommended values:</strong> 3000-5000ms</li><li><strong>Adjust based on</strong> page complexity and loading time</li></ul><hr><h3 id="browser-specific-options" tabindex="-1">🌐 Browser-Specific Options <a class="header-anchor" href="#browser-specific-options" aria-label="Permalink to “🌐 Browser-Specific Options”">​</a></h3><h4 id="direct-save-page-to-pdf-firefox-only" tabindex="-1">📌 Direct Save Page to PDF (Firefox Only) <a class="header-anchor" href="#direct-save-page-to-pdf-firefox-only" aria-label="Permalink to “📌 Direct Save Page to PDF (Firefox Only)”">​</a></h4><p><strong>Purpose:</strong> Save PDF directly without print dialog (Firefox browser only).</p><p><strong>Benefits:</strong></p><ul><li>Faster PDF generation</li><li>No print dialog interaction</li><li>Streamlined automation process</li></ul><hr><h3 id="file-management-options" tabindex="-1">📁 File Management Options <a class="header-anchor" href="#file-management-options" aria-label="Permalink to “📁 File Management Options”">​</a></h3><h4 id="if-file-already-exists-then-replace-it" tabindex="-1">📌 If file already exists then replace it <a class="header-anchor" href="#if-file-already-exists-then-replace-it" aria-label="Permalink to “📌 If file already exists then replace it”">​</a></h4><p><strong>Purpose:</strong> Control behavior when a file with the same name already exists.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Replace existing file with new content</li><li><strong>OFF</strong> - Browser will ask to replace the file</li></ul><hr><h4 id="press-ctrl-s" tabindex="-1">📌 Press Ctrl + S <a class="header-anchor" href="#press-ctrl-s" aria-label="Permalink to “📌 Press Ctrl + S”">​</a></h4><p><strong>Purpose:</strong> Use Ctrl+S keyboard shortcut for saving files.</p><p><strong>How it works:</strong></p><ul><li>Triggers browser&#39;s save dialog</li><li>Alternative to print-based PDF generation</li><li>Works with most browsers</li></ul><hr><h3 id="file-naming" tabindex="-1">📝 File Naming <a class="header-anchor" href="#file-naming" aria-label="Permalink to “📝 File Naming”">​</a></h3><h4 id="field-value" tabindex="-1">📌 Field Value <a class="header-anchor" href="#field-value" aria-label="Permalink to “📌 Field Value”">​</a></h4><p><strong>Purpose:</strong> Set custom filename for the generated PDF.</p><p><strong>Basic Usage:</strong></p><ul><li><strong>Static name:</strong> <code>document.pdf</code></li><li><strong>Dynamic name:</strong> <code>report_{$name$}.pdf</code></li><li><strong>Date-based:</strong> <code>invoice_{$date.today[yyyy-mm-dd]$}.pdf</code></li><li><strong>Unique filename:</strong> <code>document_{$date.today[x]$}.pdf</code></li></ul><p><strong>Variable Syntax:</strong></p><ul><li>Use <code>{$variable_name$}</code> format</li><li>Access form data and system variables</li><li>Create unique filenames automatically</li></ul><hr><h2 id="pdf-page-settings-firefox" tabindex="-1">🎯 PDF Page Settings (Firefox) <a class="header-anchor" href="#pdf-page-settings-firefox" aria-label="Permalink to “🎯 PDF Page Settings (Firefox)”">​</a></h2><p>Customize PDF page settings for optimal document formatting.</p><h3 id="customize-pdf-page-settings" tabindex="-1">📌 Customize PDF Page Settings <a class="header-anchor" href="#customize-pdf-page-settings" aria-label="Permalink to “📌 Customize PDF Page Settings”">​</a></h3><p><strong>Purpose:</strong> Configure page size, orientation, margins, and other PDF properties.</p><p><strong>API Reference:</strong> <a href="https://developer.mozilla.org/en-US/docs/Mozilla/Add-ons/WebExtensions/API/tabs/PageSettings" target="_blank" rel="noreferrer">PageSettings API</a></p><p><strong>Example Configuration:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>paperHeight=297,paperWidth=210,orientation=1,scaling=0.65,paperSizeUnit=1,shrinkToFit=true</span></span></code></pre></div><p><strong>Result:</strong> A4 landscape size page</p><table tabindex="0"><thead><tr><th>Property</th><th>Value</th><th>Description</th></tr></thead><tbody><tr><td><strong>paperHeight</strong></td><td>297</td><td>A4 height in mm</td></tr><tr><td><strong>paperWidth</strong></td><td>210</td><td>A4 width in mm</td></tr><tr><td><strong>orientation</strong></td><td>1</td><td>0 = portrait, 1 = landscape</td></tr><tr><td><strong>scaling</strong></td><td>0.65</td><td>Content scaling factor (default: 1)</td></tr><tr><td><strong>paperSizeUnit</strong></td><td>1</td><td>0 = inches, 1 = millimeters</td></tr><tr><td><strong>shrinkToFit</strong></td><td>true</td><td>Shrink content to fit page width</td></tr></tbody></table><h2 id="browser-window-title" tabindex="-1">🌐 Browser Window Title <a class="header-anchor" href="#browser-window-title" aria-label="Permalink to “🌐 Browser Window Title”">​</a></h2><h3 id="set-browser-window-title" tabindex="-1">📌 Set the save as browser window title name <a class="header-anchor" href="#set-browser-window-title" aria-label="Permalink to “📌 Set the save as browser window title name”">​</a></h3><p><strong>Purpose:</strong> Customize browser window title during PDF save process.</p><p><strong>Use Cases:</strong></p><ul><li><strong>Multi-language support</strong> - Different titles for different languages</li><li><strong>Branding</strong> - Custom application names</li><li><strong>Identification</strong> - Distinguish between different save operations</li></ul><p><strong>Configuration:</strong></p><ul><li>Set different titles for different languages</li><li>Use variables for dynamic titles</li><li>Match your application&#39;s branding</li></ul><hr><h3 id="set-confirm-replace-save-as-browser-window-title" tabindex="-1">📌 Set the confirm replace save as browser window title name <a class="header-anchor" href="#set-confirm-replace-save-as-browser-window-title" aria-label="Permalink to “📌 Set the confirm replace save as browser window title name”">​</a></h3><p><strong>Purpose:</strong> Customize browser window title during PDF save process.</p><p><strong>Use Cases:</strong></p><ul><li><strong>Multi-language support</strong> - Different titles for different languages</li><li><strong>Branding</strong> - Custom application names</li><li><strong>Identification</strong> - Distinguish between different save operations</li></ul><hr><h2 id="important-notes" tabindex="-1">⚠️ Important Notes <a class="header-anchor" href="#important-notes" aria-label="Permalink to “⚠️ Important Notes”">​</a></h2><h3 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to “Prerequisites”">​</a></h3><p><a href="/documentation/program.html#download-and-install">Install Helper Program</a></p><ul><li><strong>Required</strong> before using this field type</li><li><strong>Download</strong> from extension settings</li><li><strong>Install</strong> the helper program on your system</li><li><strong>Verify</strong> installation before configuring PDF fields</li></ul><h2 id="troubleshooting" tabindex="-1">🔍 Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to “🔍 Troubleshooting”">​</a></h2><table tabindex="0"><thead><tr><th>Issue</th><th>Solution</th></tr></thead><tbody><tr><td><strong>PDF not generated</strong></td><td>Check helper program installation</td></tr><tr><td><strong>Print dialog not detected</strong></td><td>Adjust image path or disable detection</td></tr><tr><td><strong>File not saved</strong></td><td>Verify save location permissions</td></tr><tr><td><strong>Filename conflicts</strong></td><td>Use unique naming with variables</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Pro Tip</p><p>Use the <code>{$date.today[x]$}</code> variable in filenames to ensure unique file names every time.</p></div><div class="warning custom-block"><p class="custom-block-title">Important</p><p>Install the helper program before using this field type. The extension will not work without it.</p></div>',87)])])}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
