import{_ as t,c as i,o as s,aj as a}from"./chunks/framework.DRmbE9yB.js";const g=JSON.parse('{"title":"Target Element For Print","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/field-types/target-element-for-print.md","filePath":"documentation/field-types/target-element-for-print.md","lastUpdated":1768407704000}'),n={name:"documentation/field-types/target-element-for-print.md"};function l(r,e,o,h,c,d){return s(),i("div",null,[...e[0]||(e[0]=[a('<h1 id="target-element-for-print" tabindex="-1">Target Element For Print <a class="header-anchor" href="#target-element-for-print" aria-label="Permalink to ‚ÄúTarget Element For Print‚Äù">‚Äã</a></h1><p>The <strong>Target Element For Print</strong> field type allows you to selectively print specific elements from a webpage while hiding all other content. This is useful when you need to print only relevant sections like receipts, invoices, forms, or specific data tables without printing the entire page.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to ‚ÄúOverview‚Äù">‚Äã</a></h2><p>When you trigger a print action (using <code>window.print()</code> or browser print), this field type ensures that only the specified elements are visible in the print output, while all other page elements are temporarily hidden.</p><p><strong>Use Cases:</strong></p><ul><li>Print only invoice or receipt sections</li><li>Print specific form data without navigation menus</li><li>Print data tables without page headers/footers</li><li>Generate clean printouts of specific content areas</li><li>Create focused print layouts for reports</li></ul><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to ‚ÄúKey Features‚Äù">‚Äã</a></h2><h3 id="üéØ-selective-printing" tabindex="-1">üéØ Selective Printing <a class="header-anchor" href="#üéØ-selective-printing" aria-label="Permalink to ‚ÄúüéØ Selective Printing‚Äù">‚Äã</a></h3><p>Target specific elements on the page to print, hiding everything else automatically.</p><h3 id="üìã-multiple-elements" tabindex="-1">üìã Multiple Elements <a class="header-anchor" href="#üìã-multiple-elements" aria-label="Permalink to ‚Äúüìã Multiple Elements‚Äù">‚Äã</a></h3><p>Select multiple elements to print by separating selectors with commas.</p><h3 id="üîç-css-selector-support" tabindex="-1">üîç CSS Selector Support <a class="header-anchor" href="#üîç-css-selector-support" aria-label="Permalink to ‚Äúüîç CSS Selector Support‚Äù">‚Äã</a></h3><p>Use standard CSS selectors (ID, class, attribute, etc.) to target elements precisely.</p><h3 id="üñ®Ô∏è-clean-output" tabindex="-1">üñ®Ô∏è Clean Output <a class="header-anchor" href="#üñ®Ô∏è-clean-output" aria-label="Permalink to ‚Äúüñ®Ô∏è Clean Output‚Äù">‚Äã</a></h3><p>Automatically hides all non-targeted elements during print, ensuring clean and professional printouts.</p><h2 id="how-to-use" tabindex="-1">How to Use <a class="header-anchor" href="#how-to-use" aria-label="Permalink to ‚ÄúHow to Use‚Äù">‚Äã</a></h2><h3 id="basic-usage" tabindex="-1">Basic Usage <a class="header-anchor" href="#basic-usage" aria-label="Permalink to ‚ÄúBasic Usage‚Äù">‚Äã</a></h3><p>Specify the CSS selector of the element you want to print in the field value.</p><p><strong>Example:</strong></p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#invoice-section</span></span></code></pre></div><p>This will print only the element with ID <code>invoice-section</code>.</p><h3 id="multiple-elements" tabindex="-1">Multiple Elements <a class="header-anchor" href="#multiple-elements" aria-label="Permalink to ‚ÄúMultiple Elements‚Äù">‚Äã</a></h3><p>To print multiple elements, separate selectors with commas (<code>,</code>).</p><p><strong>Example:</strong></p><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#receipt-header</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.invoice-details</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#payment-summary</span></span></code></pre></div><p>This will print all three elements: the receipt header, invoice details, and payment summary.</p><h3 id="common-selector-examples" tabindex="-1">Common Selector Examples <a class="header-anchor" href="#common-selector-examples" aria-label="Permalink to ‚ÄúCommon Selector Examples‚Äù">‚Äã</a></h3><table tabindex="0"><thead><tr><th>Selector Type</th><th>Example</th><th>Description</th></tr></thead><tbody><tr><td><strong>ID</strong></td><td><code>#invoice</code></td><td>Print element with ID &quot;invoice&quot;</td></tr><tr><td><strong>Class</strong></td><td><code>.receipt-section</code></td><td>Print all elements with class &quot;receipt-section&quot;</td></tr><tr><td><strong>Attribute</strong></td><td><code>[data-print=&quot;true&quot;]</code></td><td>Print elements with specific attribute</td></tr><tr><td><strong>Multiple</strong></td><td><code>#header, .content, #footer</code></td><td>Print multiple elements</td></tr><tr><td><strong>Descendant</strong></td><td><code>.container .invoice</code></td><td>Print invoice inside container</td></tr><tr><td><strong>Child</strong></td><td><code>.wrapper &gt; .receipt</code></td><td>Print direct child receipt</td></tr></tbody></table><h2 id="practical-examples" tabindex="-1">Practical Examples <a class="header-anchor" href="#practical-examples" aria-label="Permalink to ‚ÄúPractical Examples‚Äù">‚Äã</a></h2><h3 id="example-1-print-invoice-only" tabindex="-1">Example 1: Print Invoice Only <a class="header-anchor" href="#example-1-print-invoice-only" aria-label="Permalink to ‚ÄúExample 1: Print Invoice Only‚Äù">‚Äã</a></h3><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#invoice-container</span></span></code></pre></div><h3 id="example-2-print-receipt-with-header-and-footer" tabindex="-1">Example 2: Print Receipt with Header and Footer <a class="header-anchor" href="#example-2-print-receipt-with-header-and-footer" aria-label="Permalink to ‚ÄúExample 2: Print Receipt with Header and Footer‚Äù">‚Äã</a></h3><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.receipt-header</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.receipt-body</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.receipt-footer</span></span></code></pre></div><h3 id="example-3-print-multiple-sections" tabindex="-1">Example 3: Print Multiple Sections <a class="header-anchor" href="#example-3-print-multiple-sections" aria-label="Permalink to ‚ÄúExample 3: Print Multiple Sections‚Äù">‚Äã</a></h3><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#order-summary</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#customer-details</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#payment-info</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">#terms-conditions</span></span></code></pre></div><h3 id="example-4-print-table-data" tabindex="-1">Example 4: Print Table Data <a class="header-anchor" href="#example-4-print-table-data" aria-label="Permalink to ‚ÄúExample 4: Print Table Data‚Äù">‚Äã</a></h3><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.data-table</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.table-header</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.table-footer</span></span></code></pre></div><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to ‚ÄúBest Practices‚Äù">‚Äã</a></h2><h3 id="‚úÖ-do-s" tabindex="-1">‚úÖ Do&#39;s <a class="header-anchor" href="#‚úÖ-do-s" aria-label="Permalink to ‚Äú‚úÖ Do&#39;s‚Äù">‚Äã</a></h3><ul><li><strong>Use specific selectors</strong> - Target exact elements to avoid printing unwanted content</li><li><strong>Test before production</strong> - Always test print output to ensure correct elements are selected</li><li><strong>Use IDs for unique elements</strong> - IDs provide the most specific targeting</li><li><strong>Combine multiple selectors</strong> - Print related sections together for complete information</li><li><strong>Use descriptive selectors</strong> - Choose selectors that clearly identify the content</li></ul><h3 id="‚ùå-don-ts" tabindex="-1">‚ùå Don&#39;ts <a class="header-anchor" href="#‚ùå-don-ts" aria-label="Permalink to ‚Äú‚ùå Don&#39;ts‚Äù">‚Äã</a></h3><ul><li><strong>Don&#39;t use overly broad selectors</strong> - Avoid selectors like <code>div</code> or <code>*</code> that match too many elements</li><li><strong>Don&#39;t forget to test</strong> - Always verify the print output matches your expectations</li><li><strong>Don&#39;t use complex selectors</strong> - Keep selectors simple and maintainable</li><li><strong>Don&#39;t rely on dynamic content</strong> - Ensure elements exist before printing</li></ul><h2 id="important-notes" tabindex="-1">Important Notes <a class="header-anchor" href="#important-notes" aria-label="Permalink to ‚ÄúImportant Notes‚Äù">‚Äã</a></h2><div class="warning custom-block"><p class="custom-block-title">Don&#39;t Use XPath</p><p>Do <strong>not</strong> use XPath selectors in the <strong>Target Element For Print</strong> field type. Only CSS selectors are supported.</p><p><strong>Incorrect:</strong> <code>//div[@id=&#39;invoice&#39;]</code> ‚ùå<br><strong>Correct:</strong> <code>#invoice</code> ‚úÖ</p></div><div class="tip custom-block"><p class="custom-block-title">CSS Selectors Only</p><p>This field type only supports standard CSS selector syntax. Use:</p><ul><li>ID selectors: <code>#elementId</code></li><li>Class selectors: <code>.className</code></li><li>Attribute selectors: <code>[attribute=&quot;value&quot;]</code></li><li>Combinators: <code>parent &gt; child</code>, <code>ancestor descendant</code></li></ul></div><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to ‚ÄúTroubleshooting‚Äù">‚Äã</a></h2><h3 id="print-output-shows-wrong-elements" tabindex="-1">Print Output Shows Wrong Elements <a class="header-anchor" href="#print-output-shows-wrong-elements" aria-label="Permalink to ‚ÄúPrint Output Shows Wrong Elements‚Äù">‚Äã</a></h3><p><strong>Solution:</strong> Verify your CSS selector is specific enough. Use browser DevTools to test your selector:</p><div class="language-js"><button title="Copy Code" class="copy"></button><span class="lang">js</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">document.</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">querySelectorAll</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;your-selector-here&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div><h3 id="multiple-elements-not-printing" tabindex="-1">Multiple Elements Not Printing <a class="header-anchor" href="#multiple-elements-not-printing" aria-label="Permalink to ‚ÄúMultiple Elements Not Printing‚Äù">‚Äã</a></h3><p><strong>Solution:</strong> Ensure selectors are separated by commas and all elements exist on the page.</p><h3 id="nothing-prints" tabindex="-1">Nothing Prints <a class="header-anchor" href="#nothing-prints" aria-label="Permalink to ‚ÄúNothing Prints‚Äù">‚Äã</a></h3><p><strong>Solution:</strong> Check if the selector is correct and the element exists in the DOM when print is triggered.</p><h2 id="related-documentation" tabindex="-1">Related Documentation <a class="header-anchor" href="#related-documentation" aria-label="Permalink to ‚ÄúRelated Documentation‚Äù">‚Äã</a></h2><ul><li><a href="https://developer.mozilla.org/en-US/docs/Web/CSS/CSS_Selectors" target="_blank" rel="noreferrer">CSS Selector Reference</a></li><li><a href="/documentation/locate-element.html">Locate Element</a></li><li><a href="/documentation/form-fields/field-types.html">Field Types</a></li></ul>',55)])])}const m=t(n,[["render",l]]);export{g as __pageData,m as default};
