import{_ as e,c as a,o as s,aj as i}from"./chunks/framework.DRmbE9yB.js";const g=JSON.parse('{"title":"Task Scheduler","description":"","frontmatter":{},"headers":[],"relativePath":"documentation/scheduler/scheduler.md","filePath":"documentation/scheduler/scheduler.md","lastUpdated":1768407692000}'),n={name:"documentation/scheduler/scheduler.md"};function o(r,t,l,d,h,p){return s(),a("div",null,[...t[0]||(t[0]=[i(`<h1 id="task-scheduler" tabindex="-1">Task Scheduler <a class="header-anchor" href="#task-scheduler" aria-label="Permalink to ‚ÄúTask Scheduler‚Äù">‚Äã</a></h1><p>Schedule automated tasks to run at specific times or intervals for hands-free automation.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to ‚ÄúOverview‚Äù">‚Äã</a></h2><p>The Task Scheduler allows you to create and manage automated tasks that execute at predefined times or intervals. You can schedule tasks to open URLs, close tabs, and perform other actions automatically without manual intervention.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to ‚ÄúKey Features‚Äù">‚Äã</a></h2><ul><li><strong>‚è∞ Flexible Scheduling</strong> - Multiple timer options from minutes to years</li><li><strong>üîÑ Recurring Tasks</strong> - Set tasks to repeat at regular intervals</li><li><strong>üìÖ One-Time Tasks</strong> - Schedule tasks for specific dates and times</li><li><strong>üîó URL Management</strong> - Open or close URLs automatically</li><li><strong>üîî Notifications</strong> - Get notified when tasks execute</li><li><strong>üìå Tab Control</strong> - Pin tabs and auto-focus functionality</li><li><strong>‚úÖ Active/Inactive Toggle</strong> - Enable or disable tasks easily</li></ul><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to ‚ÄúGetting Started‚Äù">‚Äã</a></h2><h3 id="accessing-task-scheduler" tabindex="-1">Accessing Task Scheduler <a class="header-anchor" href="#accessing-task-scheduler" aria-label="Permalink to ‚ÄúAccessing Task Scheduler‚Äù">‚Äã</a></h3><ol><li>Open the extension options page</li><li>Navigate to <strong>Task Scheduler</strong> from the sidebar menu</li><li>You&#39;ll see the Task Scheduler interface</li></ol><h3 id="empty-state" tabindex="-1">Empty State <a class="header-anchor" href="#empty-state" aria-label="Permalink to ‚ÄúEmpty State‚Äù">‚Äã</a></h3><p>When no tasks are scheduled, you&#39;ll see:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>Not Found</span></span>
<span class="line"><span>Task Scheduler Data Not Available</span></span></code></pre></div><p><strong>Action:</strong> Click the <strong>&quot;+ Insert a new Task&quot;</strong> button to create your first task.</p><hr><h2 id="creating-a-task" tabindex="-1">Creating a Task <a class="header-anchor" href="#creating-a-task" aria-label="Permalink to ‚ÄúCreating a Task‚Äù">‚Äã</a></h2><h3 id="step-1-open-task-creation-dialog" tabindex="-1">Step 1: Open Task Creation Dialog <a class="header-anchor" href="#step-1-open-task-creation-dialog" aria-label="Permalink to ‚ÄúStep 1: Open Task Creation Dialog‚Äù">‚Äã</a></h3><p>Click the <strong>&quot;+ Insert a new Task&quot;</strong> button in the top-right corner.</p><h3 id="configuration-options" tabindex="-1">Configuration Options <a class="header-anchor" href="#configuration-options" aria-label="Permalink to ‚ÄúConfiguration Options‚Äù">‚Äã</a></h3><p>The following table shows all available configuration options for creating a task:</p><table tabindex="0"><thead><tr><th>Option</th><th>Type</th><th>Description</th><th>Required</th><th>Conditional</th></tr></thead><tbody><tr><td><a href="#task-title">Task Title</a></td><td>Text Input</td><td>Name of the task</td><td><strong>Yes</strong></td><td>-</td></tr><tr><td><a href="#type-of-task">Type of Task</a></td><td>Dropdown</td><td>Action to perform (Open URL, Close URL)</td><td><strong>Yes</strong></td><td>-</td></tr><tr><td><a href="#url">URL</a></td><td>Text Input</td><td>Target URL for the task</td><td><strong>Yes</strong></td><td>-</td></tr><tr><td><a href="#timer">Timer</a></td><td>Dropdown</td><td>When and how often task runs</td><td><strong>Yes</strong></td><td>-</td></tr><tr><td><a href="#date-time-for-every-day-and-one-time">Date &amp; Time</a></td><td>Date/Time Picker</td><td>Specific date and time</td><td>Conditional</td><td>Shows for: Every Day, One Time, Every Year</td></tr><tr><td><a href="#day-for-every-week-every-month">Day</a></td><td>Dropdown</td><td>Day selection</td><td>Conditional</td><td>Shows for: Every Week (weekdays), Every Month (1-31)</td></tr><tr><td><a href="#minute-for-custom-minute">Minute</a></td><td>Number Input</td><td>Custom minute interval</td><td>Conditional</td><td>Shows for: Custom Minute</td></tr><tr><td><a href="#seconds-for-custom-second">Seconds</a></td><td>Number Input</td><td>Custom second interval</td><td>Conditional</td><td>Shows for: Custom Second</td></tr><tr><td><a href="#task-description">Task Description</a></td><td>Textarea</td><td>Optional notes about the task</td><td>No</td><td>-</td></tr><tr><td><a href="#auto-focus-tab">Auto Focus Tab</a></td><td>Toggle Switch</td><td>Focus tab when opened</td><td>No</td><td>-</td></tr><tr><td><a href="#pinned-tab">Pinned Tab</a></td><td>Toggle Switch</td><td>Pin the tab</td><td>No</td><td>-</td></tr><tr><td><a href="#activeinactive">Active/Inactive</a></td><td>Toggle Switch</td><td>Enable or disable task</td><td>No</td><td>-</td></tr><tr><td><a href="#show-notification">Show Notification</a></td><td>Toggle Switch</td><td>Show notification on execution</td><td>No</td><td>-</td></tr></tbody></table><hr><h3 id="step-2-configure-task-settings" tabindex="-1">Step 2: Configure Task Settings <a class="header-anchor" href="#step-2-configure-task-settings" aria-label="Permalink to ‚ÄúStep 2: Configure Task Settings‚Äù">‚Äã</a></h3><h4 id="task-title" tabindex="-1">Task Title <a class="header-anchor" href="#task-title" aria-label="Permalink to ‚ÄúTask Title‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Text Input</p><p><strong>Description:</strong> Give your task a descriptive name.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>Open Dashboard Daily</span></span>
<span class="line"><span>Close Tabs Every Hour</span></span>
<span class="line"><span>Check Email Every 30 Minutes</span></span></code></pre></div><hr><h4 id="type-of-task" tabindex="-1">Type of Task <a class="header-anchor" href="#type-of-task" aria-label="Permalink to ‚ÄúType of Task‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Dropdown</p><p><strong>Description:</strong> Select the type of action to perform.</p><p><strong>Available Options:</strong></p><table tabindex="0"><thead><tr><th>Task Type</th><th>Description</th></tr></thead><tbody><tr><td><strong>Open URL</strong></td><td>Open a specific URL in a new tab</td></tr><tr><td><strong>Close URL</strong></td><td>Close tabs matching a specific URL pattern</td></tr></tbody></table><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>Open URL  - Opens https://example.com</span></span>
<span class="line"><span>Close URL - Closes all tabs with example.com</span></span></code></pre></div><hr><h4 id="url" tabindex="-1">URL <a class="header-anchor" href="#url" aria-label="Permalink to ‚ÄúURL‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Text Input</p><p><strong>Description:</strong> Enter the URL for the task.</p><p><strong>For Open URL:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>https://example.com</span></span>
<span class="line"><span>https://mail.google.com</span></span>
<span class="line"><span>https://dashboard.example.com</span></span></code></pre></div><p><strong>For Close URL:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>https://example.com</span></span>
<span class="line"><span>*example.com*  (wildcard pattern)</span></span>
<span class="line"><span>https://mail.google.com/mail/*</span></span></code></pre></div><hr><h4 id="timer" tabindex="-1">Timer <a class="header-anchor" href="#timer" aria-label="Permalink to ‚ÄúTimer‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Dropdown</p><p><strong>Description:</strong> Select when and how often the task should run.</p><p><strong>Available Timer Options:</strong></p><table tabindex="0"><thead><tr><th>Timer Option</th><th>Description</th><th>Use Case</th></tr></thead><tbody><tr><td><strong>One Time</strong></td><td>Run once at a specific date and time</td><td>One-off scheduled task</td></tr><tr><td><strong>Every Minute</strong></td><td>Run every minute</td><td>Frequent monitoring</td></tr><tr><td><strong>Every 2 Minute</strong></td><td>Run every 2 minutes</td><td>Regular checks</td></tr><tr><td><strong>Every 5 Minute</strong></td><td>Run every 5 minutes</td><td>Periodic updates</td></tr><tr><td><strong>Every 10 Minute</strong></td><td>Run every 10 minutes</td><td>Standard intervals</td></tr><tr><td><strong>Every 30 Minute</strong></td><td>Run every 30 minutes</td><td>Half-hourly tasks</td></tr><tr><td><strong>Every Hour</strong></td><td>Run every hour</td><td>Hourly automation</td></tr><tr><td><strong>Every Day</strong></td><td>Run once daily</td><td>Daily routines</td></tr><tr><td><strong>Every Week</strong></td><td>Run once weekly</td><td>Weekly tasks</td></tr><tr><td><strong>Every Month</strong></td><td>Run once monthly</td><td>Monthly operations</td></tr><tr><td><strong>Every Year</strong></td><td>Run once yearly</td><td>Annual tasks</td></tr><tr><td><strong>Custom Minute</strong></td><td>Run at custom minute intervals</td><td>Flexible timing</td></tr><tr><td><strong>Custom Second</strong></td><td>Run at custom second intervals</td><td>High-frequency tasks</td></tr><tr><td><strong>On Browser Open</strong></td><td>Run when browser starts</td><td>Startup automation</td></tr></tbody></table><hr><h4 id="date-time-for-every-day-and-one-time" tabindex="-1">Date &amp; Time (for &quot;Every Day&quot; and &quot;One Time&quot;) <a class="header-anchor" href="#date-time-for-every-day-and-one-time" aria-label="Permalink to ‚ÄúDate &amp; Time (for &quot;Every Day&quot; and &quot;One Time&quot;)‚Äù">‚Äã</a></h4><p><strong>Fields:</strong> Date Picker and Time Picker</p><p><strong>Description:</strong> Set the specific date and time for task execution.</p><p><strong>Date Format:</strong> <code>dd/mm/yyyy</code></p><p><strong>Time Format:</strong> <code>--:-- --</code> (12-hour format with AM/PM)</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>Date: 15/01/2024</span></span>
<span class="line"><span>Time: 09:30 AM</span></span></code></pre></div><p><strong>Note:</strong> This field only appears when:</p><ul><li>Timer is set to <strong>&quot;Every Day&quot;</strong> (time only)</li><li>Timer is set to <strong>&quot;One Time&quot;</strong> (date and time)</li><li>Timer is set to <strong>&quot;Every Year&quot;</strong> (date and time)</li></ul><hr><h4 id="day-for-every-week-every-month" tabindex="-1">Day (for &quot;Every Week&quot;, &quot;Every Month&quot;) <a class="header-anchor" href="#day-for-every-week-every-month" aria-label="Permalink to ‚ÄúDay (for &quot;Every Week&quot;, &quot;Every Month&quot;)‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Dropdown</p><p><strong>Description:</strong> Select the specific day for task execution. The available options change based on the Timer selection.</p><p><strong>For &quot;Every Week&quot;:</strong></p><p>Select the day of the week when the task should run.</p><p><strong>Available Options:</strong></p><ul><li>Sunday</li><li>Monday</li><li>Tuesday</li><li>Wednesday</li><li>Thursday</li><li>Friday</li><li>Saturday</li></ul><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>Timer: Every Week</span></span>
<span class="line"><span>Day: Monday</span></span>
<span class="line"><span>Time: 09:00 AM</span></span>
<span class="line"><span>Result: Task runs every Monday at 9:00 AM</span></span></code></pre></div><hr><p><strong>For &quot;Every Month&quot;:</strong></p><p>Select the day of the month (1-31) when the task should run.</p><p><strong>Available Options:</strong></p><ul><li>1 to 31 (day of the month)</li></ul><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>Timer: Every Month</span></span>
<span class="line"><span>Day: 15</span></span>
<span class="line"><span>Time: 10:00 AM</span></span>
<span class="line"><span>Result: Task runs on the 15th of every month at 10:00 AM</span></span></code></pre></div><p><strong>Note:</strong> If you select day 29, 30, or 31, the task will not run in months that don&#39;t have those days.</p><hr><h4 id="minute-for-custom-minute" tabindex="-1">Minute (for &quot;Custom Minute&quot;) <a class="header-anchor" href="#minute-for-custom-minute" aria-label="Permalink to ‚ÄúMinute (for &quot;Custom Minute&quot;)‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Number Input</p><p><strong>Description:</strong> Enter the custom interval in minutes.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>15  (runs every 15 minutes)</span></span>
<span class="line"><span>20  (runs every 20 minutes)</span></span>
<span class="line"><span>45  (runs every 45 minutes)</span></span></code></pre></div><p><strong>Note:</strong> This field only appears when Timer is set to <strong>&quot;Custom Minute&quot;</strong>.</p><hr><h4 id="seconds-for-custom-second" tabindex="-1">Seconds (for &quot;Custom Second&quot;) <a class="header-anchor" href="#seconds-for-custom-second" aria-label="Permalink to ‚ÄúSeconds (for &quot;Custom Second&quot;)‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Number Input</p><p><strong>Description:</strong> Enter the custom interval in seconds.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>30  (runs every 30 seconds)</span></span>
<span class="line"><span>45  (runs every 45 seconds)</span></span>
<span class="line"><span>90  (runs every 90 seconds)</span></span></code></pre></div><p><strong>Note:</strong> This field only appears when Timer is set to <strong>&quot;Custom Second&quot;</strong>.</p><hr><h4 id="task-description" tabindex="-1">Task Description <a class="header-anchor" href="#task-description" aria-label="Permalink to ‚ÄúTask Description‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Textarea</p><p><strong>Description:</strong> Add optional notes or description for the task.</p><p><strong>Example:</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>Opens the dashboard every morning at 9 AM</span></span>
<span class="line"><span>Closes all email tabs every hour to reduce clutter</span></span>
<span class="line"><span>Checks for new notifications every 5 minutes</span></span></code></pre></div><hr><h3 id="step-3-configure-task-options" tabindex="-1">Step 3: Configure Task Options <a class="header-anchor" href="#step-3-configure-task-options" aria-label="Permalink to ‚ÄúStep 3: Configure Task Options‚Äù">‚Äã</a></h3><h4 id="auto-focus-tab" tabindex="-1">Auto Focus Tab <a class="header-anchor" href="#auto-focus-tab" aria-label="Permalink to ‚ÄúAuto Focus Tab‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Toggle Switch</p><p><strong>Description:</strong> Automatically focus (switch to) the tab when it opens.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Tab becomes active when opened</li><li><strong>OFF</strong> - Tab opens in background</li></ul><p><strong>Use Case:</strong> Enable when you want to immediately see the opened page.</p><hr><h4 id="pinned-tab" tabindex="-1">Pinned Tab <a class="header-anchor" href="#pinned-tab" aria-label="Permalink to ‚ÄúPinned Tab‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Toggle Switch</p><p><strong>Description:</strong> Pin the tab when it opens.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Tab is pinned (smaller, stays on left)</li><li><strong>OFF</strong> - Normal tab</li></ul><p><strong>Use Case:</strong> Keep important tabs always visible and prevent accidental closure.</p><hr><h4 id="active-inactive" tabindex="-1">Active/Inactive <a class="header-anchor" href="#active-inactive" aria-label="Permalink to ‚ÄúActive/Inactive‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Toggle Switch</p><p><strong>Description:</strong> Enable or disable the task.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> (Green) - Task is active and will execute</li><li><strong>OFF</strong> (Gray) - Task is disabled and won&#39;t execute</li></ul><p><strong>Use Case:</strong> Temporarily disable tasks without deleting them.</p><hr><h4 id="show-notification" tabindex="-1">Show Notification <a class="header-anchor" href="#show-notification" aria-label="Permalink to ‚ÄúShow Notification‚Äù">‚Äã</a></h4><p><strong>Field:</strong> Toggle Switch</p><p><strong>Description:</strong> Display a notification when the task executes.</p><p><strong>Options:</strong></p><ul><li><strong>ON</strong> - Show notification on task execution</li><li><strong>OFF</strong> - Silent execution</li></ul><p><strong>Use Case:</strong> Get visual confirmation when tasks run.</p><hr><h3 id="step-4-save-the-task" tabindex="-1">Step 4: Save the Task <a class="header-anchor" href="#step-4-save-the-task" aria-label="Permalink to ‚ÄúStep 4: Save the Task‚Äù">‚Äã</a></h3><ol><li>Review all settings</li><li>Click the <strong>&quot;Save&quot;</strong> button (green)</li><li>Task will be added to the scheduler</li></ol><p><strong>Alternative:</strong> Click <strong>&quot;Close&quot;</strong> button (red) to cancel without saving.</p><hr><h2 id="managing-tasks" tabindex="-1">Managing Tasks <a class="header-anchor" href="#managing-tasks" aria-label="Permalink to ‚ÄúManaging Tasks‚Äù">‚Äã</a></h2><h3 id="viewing-tasks" tabindex="-1">Viewing Tasks <a class="header-anchor" href="#viewing-tasks" aria-label="Permalink to ‚ÄúViewing Tasks‚Äù">‚Äã</a></h3><p>All scheduled tasks appear in the main Task Scheduler interface with:</p><ul><li>Task title</li><li>Task type</li><li>Schedule information</li><li>Active/Inactive status</li><li>Action buttons</li></ul><h3 id="editing-tasks" tabindex="-1">Editing Tasks <a class="header-anchor" href="#editing-tasks" aria-label="Permalink to ‚ÄúEditing Tasks‚Äù">‚Äã</a></h3><ol><li>Click the <strong>Edit</strong> button (pencil icon) on a task</li><li>Modify task settings</li><li>Click <strong>&quot;Save&quot;</strong> to update</li></ol><h3 id="deleting-tasks" tabindex="-1">Deleting Tasks <a class="header-anchor" href="#deleting-tasks" aria-label="Permalink to ‚ÄúDeleting Tasks‚Äù">‚Äã</a></h3><ol><li>Click the <strong>Delete</strong> button (trash icon) on a task</li><li>Confirm deletion</li><li>Task will be removed permanently</li></ol><h3 id="enabling-disabling-tasks" tabindex="-1">Enabling/Disabling Tasks <a class="header-anchor" href="#enabling-disabling-tasks" aria-label="Permalink to ‚ÄúEnabling/Disabling Tasks‚Äù">‚Äã</a></h3><ol><li>Click the <strong>Active/Inactive</strong> toggle on a task</li><li>Task status changes immediately</li><li>Disabled tasks won&#39;t execute until re-enabled</li></ol><hr><h2 id="timer-options-explained" tabindex="-1">Timer Options Explained <a class="header-anchor" href="#timer-options-explained" aria-label="Permalink to ‚ÄúTimer Options Explained‚Äù">‚Äã</a></h2><h3 id="one-time" tabindex="-1">One Time <a class="header-anchor" href="#one-time" aria-label="Permalink to ‚ÄúOne Time‚Äù">‚Äã</a></h3><p><strong>Description:</strong> Execute task once at a specific date and time.</p><p><strong>Configuration:</strong></p><ul><li>Set date using date picker</li><li>Set time using time picker</li><li>Task runs once and then becomes inactive</li></ul><p><strong>Example Use Cases:</strong></p><ul><li>Schedule a meeting reminder</li><li>Open a registration page at a specific time</li><li>One-time data backup</li></ul><hr><h3 id="recurring-intervals" tabindex="-1">Recurring Intervals <a class="header-anchor" href="#recurring-intervals" aria-label="Permalink to ‚ÄúRecurring Intervals‚Äù">‚Äã</a></h3><p><strong>Every Minute / 2 / 5 / 10 / 30 Minutes:</strong></p><p><strong>Description:</strong> Task repeats at fixed minute intervals.</p><p><strong>Example:</strong></p><ul><li>Every 5 minutes: Checks for new emails</li><li>Every 30 minutes: Refreshes dashboard</li></ul><hr><h3 id="time-based-recurring" tabindex="-1">Time-Based Recurring <a class="header-anchor" href="#time-based-recurring" aria-label="Permalink to ‚ÄúTime-Based Recurring‚Äù">‚Äã</a></h3><p><strong>Every Hour / Day / Week / Month / Year:</strong></p><p><strong>Description:</strong> Task repeats at larger time intervals.</p><p><strong>For &quot;Every Day&quot;:</strong></p><ul><li>Set specific time (e.g., 9:00 AM)</li><li>Runs daily at that time</li></ul><p><strong>For &quot;Every Week&quot;:</strong></p><ul><li>Set specific day of the week (Sunday - Saturday)</li><li>Set specific time</li><li>Runs weekly on that day at that time</li></ul><p><strong>For &quot;Every Month&quot;:</strong></p><ul><li>Set specific day of the month (1-31)</li><li>Set specific time</li><li>Runs monthly on that day at that time</li></ul><p><strong>For &quot;Every Year&quot;:</strong></p><ul><li>Set specific date (dd/mm/yyyy)</li><li>Set specific time</li><li>Runs yearly on that date at that time</li></ul><p><strong>Example:</strong></p><ul><li>Every Day at 9:00 AM: Open work dashboard</li><li>Every Week on Monday at 10:00 AM: Generate weekly report</li><li>Every Month on the 15th at 2:00 PM: Review monthly data</li><li>Every Year on 01/01 at 12:00 AM: New Year automation</li></ul><hr><h3 id="custom-intervals" tabindex="-1">Custom Intervals <a class="header-anchor" href="#custom-intervals" aria-label="Permalink to ‚ÄúCustom Intervals‚Äù">‚Äã</a></h3><p><strong>Custom Minute / Custom Second:</strong></p><p><strong>Description:</strong> Define your own interval in minutes or seconds.</p><p><strong>Configuration:</strong></p><ul><li>Enter custom interval value</li><li>Task repeats at that interval</li></ul><p><strong>Example:</strong></p><ul><li>Custom Minute: 15 (runs every 15 minutes)</li><li>Custom Second: 45 (runs every 45 seconds)</li></ul><hr><h3 id="on-browser-open" tabindex="-1">On Browser Open <a class="header-anchor" href="#on-browser-open" aria-label="Permalink to ‚ÄúOn Browser Open‚Äù">‚Äã</a></h3><p><strong>Description:</strong> Task executes automatically when the browser starts.</p><p><strong>Use Cases:</strong></p><ul><li>Open frequently used pages on startup</li><li>Initialize work environment</li><li>Load essential dashboards</li></ul><hr><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to ‚ÄúUse Cases‚Äù">‚Äã</a></h2><h3 id="daily-automation" tabindex="-1">Daily Automation <a class="header-anchor" href="#daily-automation" aria-label="Permalink to ‚ÄúDaily Automation‚Äù">‚Äã</a></h3><p><strong>Scenario:</strong> Open work dashboard every morning</p><p><strong>Configuration:</strong></p><ul><li><strong>Task Title:</strong> &quot;Open Work Dashboard&quot;</li><li><strong>Type:</strong> Open URL</li><li><strong>URL:</strong> <code>https://dashboard.company.com</code></li><li><strong>Timer:</strong> Every Day</li><li><strong>Time:</strong> 09:00 AM</li><li><strong>Auto Focus:</strong> ON</li><li><strong>Pinned Tab:</strong> ON</li></ul><hr><h3 id="periodic-cleanup" tabindex="-1">Periodic Cleanup <a class="header-anchor" href="#periodic-cleanup" aria-label="Permalink to ‚ÄúPeriodic Cleanup‚Äù">‚Äã</a></h3><p><strong>Scenario:</strong> Close social media tabs every hour</p><p><strong>Configuration:</strong></p><ul><li><strong>Task Title:</strong> &quot;Close Social Media Tabs&quot;</li><li><strong>Type:</strong> Close URL</li><li><strong>URL:</strong> <code>*facebook.com*,*twitter.com*,*instagram.com*</code></li><li><strong>Timer:</strong> Every Hour</li><li><strong>Show Notification:</strong> ON</li></ul><hr><h3 id="monitoring" tabindex="-1">Monitoring <a class="header-anchor" href="#monitoring" aria-label="Permalink to ‚ÄúMonitoring‚Äù">‚Äã</a></h3><p><strong>Scenario:</strong> Check email every 5 minutes</p><p><strong>Configuration:</strong></p><ul><li><strong>Task Title:</strong> &quot;Check Email&quot;</li><li><strong>Type:</strong> Open URL</li><li><strong>URL:</strong> <code>https://mail.google.com</code></li><li><strong>Timer:</strong> Every 5 Minute</li><li><strong>Auto Focus:</strong> OFF</li><li><strong>Show Notification:</strong> OFF</li></ul><hr><h3 id="startup-automation" tabindex="-1">Startup Automation <a class="header-anchor" href="#startup-automation" aria-label="Permalink to ‚ÄúStartup Automation‚Äù">‚Äã</a></h3><p><strong>Scenario:</strong> Open essential tabs on browser start</p><p><strong>Configuration:</strong></p><ul><li><strong>Task Title:</strong> &quot;Open Essential Tabs&quot;</li><li><strong>Type:</strong> Open URL</li><li><strong>URL:</strong> <code>https://calendar.google.com</code></li><li><strong>Timer:</strong> On Browser Open</li><li><strong>Pinned Tab:</strong> ON</li></ul><hr><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to ‚ÄúBest Practices‚Äù">‚Äã</a></h2><h3 id="‚úÖ-do-s" tabindex="-1">‚úÖ Do&#39;s <a class="header-anchor" href="#‚úÖ-do-s" aria-label="Permalink to ‚Äú‚úÖ Do&#39;s‚Äù">‚Äã</a></h3><ul><li><strong>Use descriptive task titles</strong> - Make tasks easy to identify</li><li><strong>Set appropriate intervals</strong> - Don&#39;t over-schedule tasks</li><li><strong>Enable notifications for important tasks</strong> - Stay informed</li><li><strong>Use &quot;On Browser Open&quot; for startup tasks</strong> - Automate your workflow</li><li><strong>Pin important tabs</strong> - Keep essential pages accessible</li><li><strong>Test tasks before scheduling</strong> - Verify URLs and settings</li><li><strong>Disable unused tasks</strong> - Keep scheduler organized</li></ul><h3 id="‚ùå-don-ts" tabindex="-1">‚ùå Don&#39;ts <a class="header-anchor" href="#‚ùå-don-ts" aria-label="Permalink to ‚Äú‚ùå Don&#39;ts‚Äù">‚Äã</a></h3><ul><li><strong>Don&#39;t set too frequent intervals</strong> - Avoid performance issues</li><li><strong>Don&#39;t schedule conflicting tasks</strong> - Plan task timing carefully</li><li><strong>Don&#39;t forget to disable test tasks</strong> - Clean up after testing</li><li><strong>Don&#39;t use invalid URLs</strong> - Verify URLs before saving</li><li><strong>Don&#39;t over-use auto-focus</strong> - Can be disruptive</li><li><strong>Don&#39;t schedule too many tasks</strong> - Keep it manageable</li></ul><hr><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to ‚ÄúTroubleshooting‚Äù">‚Äã</a></h2><h3 id="task-not-executing" tabindex="-1">Task Not Executing <a class="header-anchor" href="#task-not-executing" aria-label="Permalink to ‚ÄúTask Not Executing‚Äù">‚Äã</a></h3><p><strong>Solutions:</strong></p><ul><li>Verify task is set to <strong>Active</strong> (toggle is ON/green)</li><li>Check timer settings are correct</li><li>Ensure browser is running at scheduled time</li><li>Verify URL is valid and accessible</li><li>Check browser permissions</li></ul><hr><h3 id="wrong-time-execution" tabindex="-1">Wrong Time Execution <a class="header-anchor" href="#wrong-time-execution" aria-label="Permalink to ‚ÄúWrong Time Execution‚Äù">‚Äã</a></h3><p><strong>Solutions:</strong></p><ul><li>Verify system time is correct</li><li>Check timezone settings</li><li>Ensure AM/PM is set correctly</li><li>Review timer interval settings</li></ul><hr><h3 id="tab-not-opening" tabindex="-1">Tab Not Opening <a class="header-anchor" href="#tab-not-opening" aria-label="Permalink to ‚ÄúTab Not Opening‚Äù">‚Äã</a></h3><p><strong>Solutions:</strong></p><ul><li>Verify URL is correct and accessible</li><li>Check browser pop-up blocker settings</li><li>Ensure extension has necessary permissions</li><li>Try opening URL manually first</li></ul><hr><h3 id="notification-not-showing" tabindex="-1">Notification Not Showing <a class="header-anchor" href="#notification-not-showing" aria-label="Permalink to ‚ÄúNotification Not Showing‚Äù">‚Äã</a></h3><p><strong>Solutions:</strong></p><ul><li>Enable &quot;Show Notification&quot; toggle</li><li>Check browser notification permissions</li><li>Verify notifications are enabled in system settings</li><li>Test with a simple task first</li></ul><hr><h2 id="tips-and-tricks" tabindex="-1">Tips and Tricks <a class="header-anchor" href="#tips-and-tricks" aria-label="Permalink to ‚ÄúTips and Tricks‚Äù">‚Äã</a></h2><h3 id="üí°-pro-tips" tabindex="-1">üí° Pro Tips <a class="header-anchor" href="#üí°-pro-tips" aria-label="Permalink to ‚Äúüí° Pro Tips‚Äù">‚Äã</a></h3><ol><li><strong>Combine with Form Filling</strong> - Schedule form filling tasks at specific times</li><li><strong>Use Browser ID</strong> - Different schedules for different browser profiles</li><li><strong>Batch Similar Tasks</strong> - Group related tasks with similar schedules</li><li><strong>Use Descriptive Names</strong> - Include time/frequency in task title</li><li><strong>Regular Review</strong> - Periodically review and update scheduled tasks</li><li><strong>Backup Tasks</strong> - Export site data to backup task configurations</li></ol><hr><h2 id="related-documentation" tabindex="-1">Related Documentation <a class="header-anchor" href="#related-documentation" aria-label="Permalink to ‚ÄúRelated Documentation‚Äù">‚Äã</a></h2><ul><li><a href="/documentation/site/site.html">Site Management</a></li><li><a href="/documentation/form-fields/field.html">Form Fields</a></li><li><a href="/documentation/field-types/timer-action.html">Timer Action Field Type</a></li><li><a href="/documentation/field-types/url-open.html">URL Open Field Type</a></li><li><a href="/documentation/settings.html">Settings</a></li></ul><hr><h2 id="support-and-help" tabindex="-1">Support and Help <a class="header-anchor" href="#support-and-help" aria-label="Permalink to ‚ÄúSupport and Help‚Äù">‚Äã</a></h2><p>If you need assistance with Task Scheduler:</p><ul><li>Check the <a href="/documentation/log.html">Log</a> for detailed error information</li><li>Visit our <a href="/documentation/#need-help">Support Channels</a> for community assistance</li><li>Report issues on our <a href="https://github.com/jeevan-lal/excellent-data-filler-docs/issues" target="_blank" rel="noreferrer">GitHub Repository</a></li></ul>`,237)])])}const u=e(n,[["render",o]]);export{g as __pageData,u as default};
